css:
.section-cover-dark-focus-z9 {
  background: linear-gradient(135deg, #e0f2f1 0%, #e3f2fd 100%);
  padding: 4rem 2rem;
  min-height: 80vh;
  display: flex;
  align-items: center;
  font-family: 'Inter', sans-serif;
}

.section-cover-dark-focus-z9 .container {
  max-width: 1100px;
  margin: 0 auto;
  width: 100%;
}

.section-cover-dark-focus-z9 .main-card {
  background-color: #2e3546;
  color: #ffffff;
  border-radius: 32px;
  padding: 3rem;
  display: grid;
  grid-template-columns: 1fr;
  gap: 3rem;
  box-shadow: 0 30px 60px -15px rgba(46, 53, 70, 0.3);
  position: relative;
  overflow: hidden;
}

@media (min-width: 992px) {
  .section-cover-dark-focus-z9 .main-card {
    grid-template-columns: 1fr 1fr;
    align-items: center;
    padding: 4rem;
  }
}

.section-cover-dark-focus-z9 .tagline {
  color: #a5b4fc;
  font-size: 0.875rem;
  font-weight: 700;
  letter-spacing: 0.05em;
  margin-bottom: 1rem;
}

.section-cover-dark-focus-z9 h1 {
  font-size: clamp(2rem, 4vw, 3.5rem);
  line-height: 1.1;
  margin-bottom: 1.5rem;
  font-weight: 700;
}

.section-cover-dark-focus-z9 p {
  color: #cbd5e1;
  font-size: 1.125rem;
  margin-bottom: 2.5rem;
  line-height: 1.6;
}

.section-cover-dark-focus-z9 .btn-row {
  display: flex;
  flex-direction: column;
  gap: 2rem;
}

@media (min-width: 576px) {
  .section-cover-dark-focus-z9 .btn-row {
    flex-direction: row;
    align-items: center;
  }
}

.section-cover-dark-focus-z9 .btn-glow {
  background: linear-gradient(135deg, #6366f1, #4f46e5);
  color: white;
  border: none;
  padding: 1rem 2rem;
  border-radius: 12px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 10px 20px -5px rgba(99, 102, 241, 0.5);
  transition: transform 0.2s;
}

.section-cover-dark-focus-z9 .btn-glow:hover {
  transform: translateY(-2px);
}

.section-cover-dark-focus-z9 .user-avatars {
  display: flex;
  align-items: center;
}

.section-cover-dark-focus-z9 .av {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  border: 2px solid #2e3546;
  margin-right: -12px;
  object-fit: cover;
}

.section-cover-dark-focus-z9 .join-text {
  margin-left: 20px;
  font-size: 0.875rem;
  color: #cbd5e1;
}

.section-cover-dark-focus-z9 .visual-col {
  position: relative;
}

.section-cover-dark-focus-z9 .feature-highlight {
  background: linear-gradient(135deg, #ffffff 0%, #f1f5f9 100%);
  border-radius: 16px;
  padding: 1rem;
  transform: perspective(1000px) rotateY(-5deg) rotateX(5deg);
  box-shadow: 0 20px 40px rgba(0,0,0,0.2);
}

.section-cover-dark-focus-z9 .feature-highlight img {
  width: 100%;
  height: auto;
  border-radius: 8px;
  display: block;
}

js:
(() => {
  const section = document.querySelector('.section-cover-dark-focus-z9');
  if (!section) return;
  section.querySelectorAll('img').forEach(img => {
     if(img.src.startsWith('https://pixabay.com')) {
        fetch(img.src).then(r=>r.json()).then(d=>{
           if(d.hits && d.hits.length) img.src = d.hits[0].webformatURL;
        });
     }
  });
})();
