css:
.section-cover-dark-social-3 {
  background-color: #121212;
  color: #fff;
  padding: 6rem 1rem;
  font-family: var(--font-body);
  position: relative;
}
.section-cover-dark-social-3 .container {
  max-width: 1100px;
  margin: 0 auto;
}
.section-cover-dark-social-3 .split-wrapper {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 3rem;
  align-items: center;
}
.section-cover-dark-social-3 h2 {
  font-family: var(--font-heading);
  font-size: 2.5rem;
  margin-bottom: 2rem;
  background: linear-gradient(90deg, #fff, #aaa);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}
.section-cover-dark-social-3 .card-stack {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}
.section-cover-dark-social-3 .trust-card {
  background: #1e1e1e;
  padding: 1.5rem;
  border-radius: 8px;
  border: 1px solid #333;
  opacity: 0.7;
  transition: 0.3s;
}
.section-cover-dark-social-3 .trust-card.highlight {
  background: #252525;
  border-color: var(--color-primary);
  opacity: 1;
  transform: scale(1.02);
  box-shadow: 0 0 20px rgba(0,0,0,0.5);
}
.section-cover-dark-social-3 .card-top {
  display: flex;
  justify-content: space-between;
  font-size: 0.8rem;
  color: #888;
  margin-bottom: 0.5rem;
}
.section-cover-dark-social-3 .stars {
  color: gold;
}
.section-cover-dark-social-3 .image-side {
  position: relative;
  height: 400px;
}
.section-cover-dark-social-3 .main-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 16px;
  filter: brightness(0.8);
}
.section-cover-dark-social-3 .overlay-badge {
  position: absolute;
  bottom: -20px;
  left: -20px;
  background: var(--color-primary);
  color: #000;
  padding: 1.5rem;
  border-radius: 12px;
  box-shadow: 0 10px 30px rgba(0,0,0,0.5);
  text-align: center;
}
.section-cover-dark-social-3 .overlay-badge .number {
  display: block;
  font-size: 1.5rem;
  font-weight: 800;
}
.section-cover-dark-social-3 .overlay-badge .label {
  font-size: 0.8rem;
  text-transform: uppercase;
  font-weight: 600;
}
@media (max-width: 768px) {
  .section-cover-dark-social-3 .split-wrapper {
    grid-template-columns: 1fr;
  }
  .section-cover-dark-social-3 .image-side {
    order: -1;
    height: 250px;
  }
}
