css:
.cover-dringender-service-section {
    background-color: var(--color-light, #FFFFFF);
    padding: 6rem 0;
}
.cover-dringender-service-section .container {
    width: 90%;
    max-width: 900px;
    margin: 0 auto;
    padding: 0 1rem;
    text-align: center;
}
.cover-dringender-service-section h1 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-dringender-service-section .subtitle {
    font-size: 1.1rem;
    max-width: 600px;
    margin: 0 auto 2.5rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.cover-dringender-service-section .btn-dark {
    display: inline-block;
    padding: 1rem 2.5rem;
    text-decoration: none;
    border-radius: 0.5rem;
    font-weight: 600;
    transition: all 0.3s ease;
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
.cover-dringender-service-section .btn-dark:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}
.cover-dringender-service-section .image-wrapper-dringend {
    margin-top: 4rem;
    border-radius: 1.5rem;
    overflow: hidden;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
}
.cover-dringender-service-section .image-wrapper-dringend img {
    width: 100%;
    height: auto;
    display: block;
}
.cover-dringender-service-section .trust-text {
    margin-top: 3rem;
    font-size: 1rem;
    color: var(--color-secondary, #6B7280);
}
.cover-dringender-service-section .logos-container-dringend {
    margin-top: 1.5rem;
    overflow: hidden;
    -webkit-mask-image: linear-gradient(to right, transparent, black 10%, black 90%, transparent);
    mask-image: linear-gradient(to right, transparent, black 10%, black 90%, transparent);
}
.cover-dringender-service-section .logo-scroller-track-dringend {
    display: flex;
    align-items: center;
    gap: 4rem;
    width: max-content;
    animation: scroll-dringend 40s linear infinite;
}
.cover-dringender-service-section .logos-container-dringend img {
    height: 28px;
    width: auto;
    filter: grayscale(100%);
    opacity: 0.7;
}
@keyframes scroll-dringend {
    from { transform: translateX(0); }
    to { transform: translateX(-50%); }
}
.cover-dringender-service-section .anim-delay-1 { transition-delay: 0.1s; }
.cover-dringender-service-section .anim-delay-2 { transition-delay: 0.2s; }
.cover-dringender-service-section .anim-delay-3 { transition-delay: 0.3s; }
.cover-dringender-service-section .anim-delay-4 { transition-delay: 0.4s; }
