css:
.cover-1-section {
    position: relative;
    min-height: 80vh;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 5rem 0;
    color: var(--color-light, #FFFFFF);
    text-align: center;
    overflow: hidden;
}
.cover-1-section .background-image-wrapper {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -2;
}
.cover-1-section .background-image-wrapper img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.cover-1-section .overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(45, 55, 71, 0.8); /* a dark slate color */
    z-index: -1;
}
.cover-1-section .container { width: 90%; max-width: 800px; margin: 0 auto; padding: 0 1rem; position: relative; z-index: 1; }
.cover-1-section .rating { margin-bottom: 1.5rem; }
.cover-1-section .stars { color: #f59e0b; /* amber-500 */ margin-bottom: 0.5rem; font-size: 1.25rem; }
.cover-1-section .rating p { font-size: 0.9rem; opacity: 0.8; }
.cover-1-section h1 {
    font-size: clamp(2.5rem, 6vw, 4rem);
    font-weight: 700;
    line-height: 1.2;
    margin-bottom: 1.5rem;
}
.cover-1-section .subtitle {
    font-size: 1.1rem;
    max-width: 600px;
    margin: 0 auto 2.5rem;
    opacity: 0.9;
    line-height: 1.7;
}
.cover-1-section .btn {
    display: inline-block;
    padding: 0.8rem 2.5rem;
    text-decoration: none;
    border-radius: 0.375rem;
    font-weight: 600;
    transition: all 0.3s ease;
}
.cover-1-section .btn-light {
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #111827);
}
.cover-1-section .btn-light:hover {
    background-color: #e5e7eb; /* gray-200 */
    transform: translateY(-2px);
}
.cover-1-section .anim-delay-1 { transition-delay: 0.1s; }
.cover-1-section .anim-delay-2 { transition-delay: 0.2s; }
.cover-1-section .anim-delay-3 { transition-delay: 0.3s; }
