css:
.cover-erfolgsplan-section {
    padding: 6rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-erfolgsplan-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.cover-erfolgsplan-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
}
@media (min-width: 992px) {
    .cover-erfolgsplan-container {
        grid-template-columns: 1fr 1fr;
    }
}
.cover-erfolgsplan-section h1 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-erfolgsplan-section p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
    margin-bottom: 2rem;
}
.cover-erfolgsplan-section .email-form {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    margin-bottom: 2rem;
}
@media (min-width: 576px) {
    .cover-erfolgsplan-section .email-form {
        flex-direction: row;
    }
}
.cover-erfolgsplan-section .email-form input {
    flex-grow: 1;
    padding: 0.75rem 1rem;
    border: 1px solid #d1d5db; /* gray-300 */
    border-radius: 0.5rem;
    font-size: 1rem;
}
.cover-erfolgsplan-section .btn-dark {
    padding: 0.75rem 1.5rem;
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    border: none;
    border-radius: 0.5rem;
    font-weight: 600;
    cursor: pointer;
    transition: background-color 0.3s;
}
.cover-erfolgsplan-section .features-list {
    display: flex;
    flex-wrap: wrap;
    gap: 1.5rem;
}
.cover-erfolgsplan-section .feature-item {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    color: var(--color-secondary, #6B7280);
    font-size: 0.9rem;
}
.cover-erfolgsplan-section .feature-item i {
    color: var(--color-dark, #1F2937);
}
.cover-erfolgsplan-section .image-side img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 1.5rem;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
}
