css:
.section-cover-floating-dish-right {
  background-color: #0f0f0f;
  color: #f0f0f0;
  padding: 80px 0;
  overflow: visible;
}
.section-cover-floating-dish-right .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}
.section-cover-floating-dish-right .wrapper {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 50px;
}
.section-cover-floating-dish-right .text-content {
  flex: 1;
  max-width: 550px;
}
.section-cover-floating-dish-right .dash-line {
  width: 60px;
  height: 4px;
  background: #ff4500;
  margin-bottom: 24px;
}
.section-cover-floating-dish-right h1 {
  font-family: var(--font-heading, serif);
  font-size: 3.5rem;
  line-height: 1.15;
  margin-bottom: 24px;
}
.section-cover-floating-dish-right .highlight {
  color: #ff4500;
}
.section-cover-floating-dish-right p {
  font-size: 1.1rem;
  color: #888;
  margin-bottom: 32px;
  line-height: 1.6;
}
.section-cover-floating-dish-right .features {
  list-style: none;
  padding: 0;
  margin-bottom: 40px;
  display: flex;
  gap: 20px;
  flex-wrap: wrap;
}
.section-cover-floating-dish-right .features li {
  font-size: 0.95rem;
  color: #ccc;
}
.section-cover-floating-dish-right .btn-solid {
  background: #ff4500;
  color: white;
  padding: 14px 36px;
  border-radius: 4px;
  text-decoration: none;
  font-weight: bold;
  text-transform: uppercase;
  letter-spacing: 1px;
  transition: background 0.3s;
}
.section-cover-floating-dish-right .btn-solid:hover {
  background: #d63a00;
}
.section-cover-floating-dish-right .visual-content {
  flex: 1;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
}
.section-cover-floating-dish-right .glow-bg {
  position: absolute;
  width: 300px;
  height: 300px;
  background: radial-gradient(circle, rgba(255,69,0,0.3) 0%, rgba(0,0,0,0) 70%);
  z-index: 0;
}
.section-cover-floating-dish-right .dish-img {
  width: 100%;
  max-width: 450px;
  aspect-ratio: 1;
  object-fit: cover;
  border-radius: 50%;
  position: relative;
  z-index: 1;
  animation: float 6s ease-in-out infinite;
}
@keyframes float {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-20px); }
}
.section-cover-floating-dish-right .card-float {
  position: absolute;
  bottom: 40px;
  right: 20px;
  background: #1e1e1e;
  padding: 15px 25px;
  border-radius: 12px;
  box-shadow: 0 10px 30px rgba(0,0,0,0.5);
  z-index: 2;
  border-left: 3px solid #ff4500;
}
.section-cover-floating-dish-right .card-title {
  display: block;
  font-weight: bold;
  color: #fff;
  font-size: 0.9rem;
}
.section-cover-floating-dish-right .card-price {
  display: block;
  color: #ff4500;
  font-weight: 700;
  font-size: 1.1rem;
}
@media (max-width: 850px) {
  .section-cover-floating-dish-right .wrapper {
    flex-direction: column;
    text-align: center;
  }
  .section-cover-floating-dish-right .dash-line,
  .section-cover-floating-dish-right .features {
    margin-left: auto;
    margin-right: auto;
    justify-content: center;
  }
}
