css:
.cover-ugc-section {
    position: relative;
    min-height: 80vh;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 5rem 2rem;
    color: var(--color-light, #FFFFFF);
    text-align: center;
    overflow: hidden;
}
.cover-ugc-section .background-image-wrapper { position: absolute; top: 0; left: 0; width: 100%; height: 100%; z-index: -2; }
.cover-ugc-section .background-image-wrapper img { width: 100%; height: 100%; object-fit: cover; filter: blur(2px); }
.cover-ugc-section .overlay { position: absolute; top: 0; left: 0; width: 100%; height: 100%; background-color: rgba(0, 0, 0, 0.5); z-index: -1; }
.cover-ugc-section .container { width: 90%; max-width: 1000px; margin: 0 auto; position: relative; z-index: 1; }
.cover-ugc-section .content-wrapper { display: grid; grid-template-columns: 1fr; gap: 2rem; align-items: center; }
@media (min-width: 768px) { .cover-ugc-section .content-wrapper { grid-template-columns: 1fr 1fr; text-align: left; } }
.cover-ugc-section h1 { font-size: clamp(2.5rem, 6vw, 4rem); font-weight: 700; line-height: 1.2; }
.cover-ugc-section .subtitle { font-size: 1.1rem; margin: 0 0 2rem; opacity: 0.9; line-height: 1.7; }
.cover-ugc-section .cta-group { display: flex; gap: 1rem; justify-content: center; }
@media (min-width: 768px) { .cover-ugc-section .cta-group { justify-content: flex-start; } }
.cover-ugc-section .btn { display: inline-block; padding: 0.8rem 2rem; text-decoration: none; border-radius: 0.375rem; font-weight: 600; transition: all 0.3s ease; border: 1px solid var(--color-light, #FFFFFF); }
.cover-ugc-section .btn-light { background-color: var(--color-light, #FFFFFF); color: var(--color-dark, #111827); }
.cover-ugc-section .btn-transparent { background-color: transparent; color: var(--color-light, #FFFFFF); }
.cover-ugc-section .btn-transparent:hover { background-color: rgba(255, 255, 255, 0.1); }
