css:
.cover-geheimnis-wachstum-section {
    background-color: var(--color-light, #FFFFFF);
    padding: 6rem 0;
}
.cover-geheimnis-wachstum-section .container { width: 90%; max-width: 900px; margin: 0 auto; padding: 0 1rem; }
.cover-geheimnis-wachstum-section .hero-part { margin-bottom: 4rem; text-align: center; }
.cover-geheimnis-wachstum-section h1 {
    font-size: clamp(2.5rem, 6vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    margin-bottom: 1.5rem;
    color: var(--color-dark, #1F2937);
}
.cover-geheimnis-wachstum-section .subtitle {
    font-size: 1.1rem;
    max-width: 600px;
    margin: 0 auto 2.5rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.cover-geheimnis-wachstum-section .btn {
    display: inline-block;
    padding: 1rem 2.5rem;
    text-decoration: none;
    border-radius: 0.5rem;
    font-weight: 600;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
.cover-geheimnis-wachstum-section .btn-dark {
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
}
.cover-geheimnis-wachstum-section .btn-dark:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}
.cover-geheimnis-wachstum-section .features-part-geheimnis {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
}
.cover-geheimnis-wachstum-section .feature-card-geheimnis {
    background-color: #f3f4f6; /* gray-100 */
    padding: 2rem;
    border-radius: 1rem;
    text-align: left;
}
.cover-geheimnis-wachstum-section .feature-card-geheimnis h3 {
    font-size: 1.25rem;
    font-weight: 600;
    margin-bottom: 0.75rem;
    color: var(--color-dark, #1F2937);
}
.cover-geheimnis-wachstum-section .feature-card-geheimnis p {
    font-size: 0.95rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
}
.cover-geheimnis-wachstum-section .anim-delay-1 { transition-delay: 0.1s; }
.cover-geheimnis-wachstum-section .anim-delay-2 { transition-delay: 0.2s; }
.cover-geheimnis-wachstum-section .anim-delay-3 { transition-delay: 0.3s; }
.cover-geheimnis-wachstum-section .anim-delay-4 { transition-delay: 0.4s; }
