css:
.section-cover-glass-overlay-5 {
  position: relative;
  padding: 6rem 1rem;
  overflow: hidden;
  font-family: var(--font-body);
  color: #fff;
}
.section-cover-glass-overlay-5 .bg-image-container {
  position: absolute;
  inset: 0;
  z-index: 0;
}
.section-cover-glass-overlay-5 .bg-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}
.section-cover-glass-overlay-5 .container {
  position: relative;
  z-index: 1;
  max-width: 1200px;
  margin: 0 auto;
}
.section-cover-glass-overlay-5 .glass-content {
  display: grid;
  grid-template-columns: 1.5fr 1fr;
  gap: 4rem;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  padding: 3rem;
  border-radius: 24px;
  border: 1px solid rgba(255, 255, 255, 0.2);
}
.section-cover-glass-overlay-5 h1 {
  font-family: var(--font-heading);
  font-size: 3rem;
  margin-bottom: 1rem;
}
.section-cover-glass-overlay-5 p {
  font-size: 1.2rem;
  margin-bottom: 2rem;
  opacity: 0.9;
}
.section-cover-glass-overlay-5 .btn-glass {
  display: inline-block;
  padding: 0.8rem 2rem;
  background: rgba(255,255,255,0.2);
  border: 1px solid rgba(255,255,255,0.4);
  color: #fff;
  text-decoration: none;
  border-radius: 50px;
  transition: 0.3s;
}
.section-cover-glass-overlay-5 .btn-glass:hover {
  background: #fff;
  color: var(--color-primary);
}
.section-cover-glass-overlay-5 .cards-col {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  justify-content: center;
}
.section-cover-glass-overlay-5 .glass-card {
  background: rgba(0, 0, 0, 0.3);
  padding: 1.5rem;
  border-radius: 12px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.section-cover-glass-overlay-5 .glass-card strong {
  font-size: 1.5rem;
  color: var(--color-primary);
}
@media (max-width: 768px) {
  .section-cover-glass-overlay-5 .glass-content {
    grid-template-columns: 1fr;
  }
}
