.section-cover-gradient-testimonials-v1 {
  --color-bg-start: #e0f2f1;
  --color-bg-end: #e3f2fd;
  --color-card-gradient-start: #a1c4fd;
  --color-card-gradient-end: #c2e9fb;
  --color-dark: #2e3546;
  --color-text-dark: #1f2937;
  --color-text-light: #ffffff;
  --font-family-base: 'Inter', sans-serif;
  --font-family-script: 'Dancing Script', cursive;

  position: relative;
  padding: 6rem 2rem;
  background: linear-gradient(135deg, var(--color-bg-start), var(--color-bg-end));
  font-family: var(--font-family-base);
  overflow: hidden;
}

.section-cover-gradient-testimonials-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
  position: relative;
  z-index: 2;
}

.section-cover-gradient-testimonials-v1 .header-group {
  text-align: center;
  margin-bottom: 4rem;
  position: relative;
}

.section-cover-gradient-testimonials-v1 .badge {
  background-color: var(--color-dark);
  color: var(--color-text-light);
  padding: 0.5rem 1.5rem;
  border-radius: 50px;
  text-transform: uppercase;
  font-size: 0.75rem;
  letter-spacing: 1px;
  font-weight: 600;
  display: inline-block;
  margin-bottom: 1rem;
}

.section-cover-gradient-testimonials-v1 .watermark-title {
  font-family: var(--font-family-script, serif);
  font-size: clamp(3rem, 8vw, 6rem);
  color: rgba(255, 255, 255, 0.6);
  margin: 0;
  line-height: 1;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  white-space: nowrap;
  z-index: -1;
  pointer-events: none;
}

.section-cover-gradient-testimonials-v1 .subtitle {
  color: rgba(46, 53, 70, 0.1);
  font-size: 1.125rem;
  max-width: 600px;
  margin: 3rem auto 0;
  opacity: 0.05;
  /* Mimicking the hidden text in reference */
  display: none;
}

.section-cover-gradient-testimonials-v1 .grid-layout {
  display: grid;
  grid-template-columns: 1fr;
  gap: 2rem;
}

@media (min-width: 992px) {
  .section-cover-gradient-testimonials-v1 .grid-layout {
    grid-template-columns: 1.2fr 0.8fr;
  }
}

/* Large Card */
.section-cover-gradient-testimonials-v1 .card-large {
  background: linear-gradient(135deg, #9acae8 0%, #b0eac6 100%);
  border-radius: 24px;
  padding: 3rem;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  box-shadow: 0 20px 40px -10px rgba(0,0,0,0.1);
  color: #fff;
}

.section-cover-gradient-testimonials-v1 .rating-badge {
  background: #2e3546;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;
  border-radius: 50px;
  align-self: flex-start;
  margin-bottom: 2rem;
  font-size: 0.875rem;
}

.section-cover-gradient-testimonials-v1 .stars {
  color: #ffb700;
}

.section-cover-gradient-testimonials-v1 blockquote {
  font-size: 1.5rem;
  line-height: 1.4;
  margin: 0 0 2rem 0;
  font-weight: 500;
}

.section-cover-gradient-testimonials-v1 .author {
  display: flex;
  align-items: center;
  gap: 1rem;
}

.section-cover-gradient-testimonials-v1 .avatar {
  width: 60px;
  height: 60px;
  border-radius: 50%;
  object-fit: cover;
  border: 2px solid rgba(255,255,255,0.3);
}

.section-cover-gradient-testimonials-v1 .author-info strong {
  display: block;
  font-size: 1rem;
}

.section-cover-gradient-testimonials-v1 .author-info span {
  font-size: 0.875rem;
  opacity: 0.9;
}

/* Right Column */
.section-cover-gradient-testimonials-v1 .column-right {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.section-cover-gradient-testimonials-v1 .card-dark {
  background-color: var(--color-dark);
  color: var(--color-text-light);
  padding: 2rem;
  border-radius: 20px;
  box-shadow: 0 10px 30px -5px rgba(0,0,0,0.15);
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.section-cover-gradient-testimonials-v1 .card-dark blockquote {
  font-size: 1rem;
  line-height: 1.6;
  opacity: 0.9;
  margin: 0 0 1.5rem 0;
  font-style: normal;
}

.section-cover-gradient-testimonials-v1 .author-mini strong {
  display: block;
  font-size: 0.95rem;
}

.section-cover-gradient-testimonials-v1 .author-mini span {
  font-size: 0.8rem;
  color: rgba(255,255,255,0.6);
}

js:
(() => {
  const section = document.querySelector('.section-cover-gradient-testimonials-v1');
  if (!section) return;

  // Pixabay Image Fetcher
  const images = section.querySelectorAll('img');
  images.forEach(img => {
    if (img.src.startsWith('https://pixabay.com/api/')) {
      fetch(img.src)
        .then(res => res.json())
        .then(data => {
          if (data.hits && data.hits.length > 0) {
            img.src = data.hits[0].webformatURL;
          } else {
            img.src = 'https://via.placeholder.com/150'; // Fallback
          }
        })
        .catch(err => console.error('VisionAi Image Fetch Error:', err));
    }
  });
})();
