css:
.section-cover-interactive-slider-4 {
  padding: 5rem 1rem;
  background: #f9f9f9;
  font-family: var(--font-body);
  position: relative;
}
.section-cover-interactive-slider-4 .container {
  max-width: 1200px;
  margin: 0 auto;
}
.section-cover-interactive-slider-4 .cover-flex {
  display: flex;
  gap: 4rem;
  align-items: center;
}
.section-cover-interactive-slider-4 .interactive-list {
  flex: 1;
}
.section-cover-interactive-slider-4 h2 {
  font-family: var(--font-heading);
  font-size: 2.5rem;
  margin-bottom: 2rem;
  color: var(--color-dark);
}
.section-cover-interactive-slider-4 .tab-item {
  padding: 1.5rem;
  border-left: 4px solid #ddd;
  cursor: pointer;
  transition: all 0.3s;
  margin-bottom: 1rem;
}
.section-cover-interactive-slider-4 .tab-item:hover {
  background: #fff;
}
.section-cover-interactive-slider-4 .tab-item.active {
  border-left-color: var(--color-primary);
  background: #fff;
  box-shadow: 0 5px 15px rgba(0,0,0,0.05);
}
.section-cover-interactive-slider-4 .tab-item h3 {
  margin: 0 0 0.5rem 0;
  color: var(--color-dark);
}
.section-cover-interactive-slider-4 .tab-item p {
  margin: 0;
  color: var(--color-secondary);
  font-size: 0.9rem;
}
.section-cover-interactive-slider-4 .image-display {
  flex: 1;
  height: 450px;
  position: relative;
  border-radius: 20px;
  overflow: hidden;
  box-shadow: 0 20px 40px rgba(0,0,0,0.1);
}
.section-cover-interactive-slider-4 .slide-img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  opacity: 0;
  transition: opacity 0.5s ease;
}
.section-cover-interactive-slider-4 .slide-img.active {
  opacity: 1;
}
@media (max-width: 768px) {
  .section-cover-interactive-slider-4 .cover-flex {
    flex-direction: column;
  }
  .section-cover-interactive-slider-4 .image-display {
    width: 100%;
    height: 300px;
  }
}
