css:
.cover-klueger-wachsen-section {
    background-color: #f8f9fa; /* A very light gray */
    padding: 6rem 0;
}
.cover-klueger-wachsen-section .container {
    width: 90%;
    max-width: 800px;
    margin: 0 auto;
    padding: 0 1rem;
    text-align: center;
}
.cover-klueger-wachsen-section h1 {
    font-size: clamp(2.5rem, 6vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-klueger-wachsen-section .subtitle {
    font-size: 1.1rem;
    max-width: 600px;
    margin: 0 auto 2.5rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.cover-klueger-wachsen-section .btn-dark {
    display: inline-block;
    padding: 1rem 2.5rem;
    text-decoration: none;
    border-radius: 0.5rem;
    font-weight: 600;
    transition: all 0.3s ease;
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
.cover-klueger-wachsen-section .btn-dark:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}
.cover-klueger-wachsen-section .features-list-klueger {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 2rem;
    margin-top: 3rem;
    flex-wrap: wrap;
}
.cover-klueger-wachsen-section .feature-item-klueger {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 1rem;
    color: var(--color-secondary, #6B7280);
}
.cover-klueger-wachsen-section .feature-item-klueger i {
    color: var(--color-dark, #1F2937);
}
.cover-klueger-wachsen-section .anim-delay-1 { transition-delay: 0.1s; }
.cover-klueger-wachsen-section .anim-delay-2 { transition-delay: 0.2s; }
.cover-klueger-wachsen-section .anim-delay-3 { transition-delay: 0.3s; }
