css:
.cover-4-section {
    background-color: var(--color-light, #FFFFFF);
    padding: 8rem 0;
}
.cover-4-section .container { width: 90%; max-width: 800px; margin: 0 auto; padding: 0 1rem; text-align: center; }
.cover-4-section h1 {
    font-size: clamp(2.5rem, 6vw, 4rem);
    font-weight: 700;
    line-height: 1.2;
    margin-bottom: 1.5rem;
    color: var(--color-dark, #1F2937);
}
.cover-4-section .subtitle {
    font-size: 1.1rem;
    max-width: 600px;
    margin: 0 auto 2.5rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.cover-4-section .btn {
    display: inline-block;
    padding: 1rem 2.5rem;
    text-decoration: none;
    border-radius: 0.5rem;
    font-weight: 600;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
.cover-4-section .btn-dark {
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
}
.cover-4-section .btn-dark:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}
.cover-4-section .anim-delay-1 { transition-delay: 0.1s; }
.cover-4-section .anim-delay-2 { transition-delay: 0.2s; }
