css:
.section-cover-minimal-dark-type {
  background-color: #000;
  color: #fff;
  padding: 100px 0;
  font-family: var(--font-body, sans-serif);
}
.section-cover-minimal-dark-type .container {
  max-width: 1000px;
  margin: 0 auto;
  padding: 0 20px;
}
.section-cover-minimal-dark-type h1 {
  font-size: clamp(4rem, 12vw, 8rem);
  font-weight: 900;
  line-height: 0.9;
  letter-spacing: -2px;
  margin-bottom: 40px;
  color: #fff;
}
.section-cover-minimal-dark-type .sub-group {
  display: flex;
  justify-content: space-between;
  align-items: flex-end;
  margin-bottom: 60px;
  flex-wrap: wrap;
  gap: 30px;
  border-top: 1px solid #333;
  padding-top: 30px;
}
.section-cover-minimal-dark-type p {
  max-width: 400px;
  color: #888;
  font-size: 1.1rem;
}
.section-cover-minimal-dark-type .btn-border {
  color: #ff4500;
  border: 1px solid #ff4500;
  padding: 12px 30px;
  border-radius: 50px;
  text-decoration: none;
  transition: all 0.3s;
}
.section-cover-minimal-dark-type .btn-border:hover {
  background: #ff4500;
  color: white;
}
.section-cover-minimal-dark-type .image-strip {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
}
.section-cover-minimal-dark-type .image-strip img {
  width: 100%;
  height: 200px;
  object-fit: cover;
  border-radius: 4px;
  filter: grayscale(100%);
  transition: filter 0.4s;
}
.section-cover-minimal-dark-type .image-strip img:hover {
  filter: grayscale(0%);
}
@media (max-width: 600px) {
  .section-cover-minimal-dark-type .image-strip {
    grid-template-columns: 1fr;
  }
}
