css:
.section-cover-minimal-typography-10 {
  padding: 6rem 1rem;
  background: #fff;
  color: #000;
  font-family: var(--font-body);
  position: relative;
}
.section-cover-minimal-typography-10 .container {
  max-width: 1000px;
  margin: 0 auto;
}
.section-cover-minimal-typography-10 .minimal-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 3rem;
  align-items: start;
}
.section-cover-minimal-typography-10 h1 {
  font-family: var(--font-heading);
  font-size: 4rem;
  line-height: 1;
  margin-bottom: 2rem;
  letter-spacing: -1px;
}
.section-cover-minimal-typography-10 .divider {
  width: 50px;
  height: 4px;
  background: #000;
  margin-bottom: 3rem;
}
.section-cover-minimal-typography-10 .card-stack {
  display: flex;
  gap: 2rem;
}
.section-cover-minimal-typography-10 .mini-card {
  border-left: 1px solid #ddd;
  padding-left: 1rem;
}
.section-cover-minimal-typography-10 .mini-card strong {
  display: block;
  font-size: 1.2rem;
  margin-bottom: 0.5rem;
}
.section-cover-minimal-typography-10 .mini-card p {
  font-size: 0.9rem;
  color: #666;
  margin: 0;
}
.section-cover-minimal-typography-10 .image-mask {
  width: 100%;
  height: 500px;
  background: #f4f4f4;
}
.section-cover-minimal-typography-10 .minimal-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  filter: grayscale(100%);
  transition: filter 0.5s;
}
.section-cover-minimal-typography-10 .minimal-img:hover {
  filter: grayscale(0%);
}
@media (max-width: 768px) {
  .section-cover-minimal-typography-10 .minimal-grid {
    grid-template-columns: 1fr;
  }
  .section-cover-minimal-typography-10 h1 {
    font-size: 3rem;
  }
  .section-cover-minimal-typography-10 .image-mask {
    height: 300px;
  }
}
