css:
.cover-bewertung-section {
    padding: 6rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-bewertung-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.cover-bewertung-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
}
@media (min-width: 992px) {
    .cover-bewertung-container {
        grid-template-columns: 1fr 1fr;
    }
}
.cover-bewertung-section .content-side {
    max-width: 450px;
}
.cover-bewertung-section h1 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-bewertung-section .content-side p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
    margin-bottom: 2rem;
}
.cover-bewertung-section .rating-wrapper {
    display: flex;
    align-items: center;
    gap: 1rem;
    margin-bottom: 2.5rem;
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
}
.cover-bewertung-section .avatars {
    display: flex;
}
.cover-bewertung-section .avatars img {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    border: 2px solid var(--color-light, #FFFFFF);
    margin-left: -12px;
}
.cover-bewertung-section .stars {
    color: #f59e0b; /* amber-500 */
}
.cover-bewertung-section .btn-dark {
    display: inline-block;
    padding: 0.75rem 2rem;
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    border-radius: 0.5rem;
    font-weight: 600;
    text-decoration: none;
    transition: all 0.3s;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
.cover-bewertung-section .image-side img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 1.5rem;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
}
