css:
.cover-checkliste-section {
    padding: 6rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-checkliste-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.cover-checkliste-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
}
@media (min-width: 992px) {
    .cover-checkliste-container {
        grid-template-columns: 1fr 1fr;
    }
}
.cover-checkliste-section .content-side {
    max-width: 450px;
}
.cover-checkliste-section h1 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-checkliste-section p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
    margin-bottom: 2rem;
}
.cover-checkliste-section .btn-dark {
    display: inline-block;
    padding: 0.75rem 2rem;
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    border-radius: 0.5rem;
    font-weight: 600;
    text-decoration: none;
    transition: all 0.3s;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
    margin-bottom: 2.5rem;
}
.cover-checkliste-section .features-list {
    display: flex;
    flex-wrap: wrap;
    gap: 1.5rem;
}
.cover-checkliste-section .feature-item {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    color: var(--color-dark, #1F2937);
    font-size: 0.9rem;
    font-weight: 500;
}
.cover-checkliste-section .feature-item i {
    color: var(--color-dark, #1F2937);
}
.cover-checkliste-section .image-side img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 1.5rem;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
}
