css:
.cover-version-tag-section {
    padding: 6rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-version-tag-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.cover-version-tag-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
}
@media (min-width: 992px) {
    .cover-version-tag-container {
        grid-template-columns: 1fr 1fr;
    }
}
.cover-version-tag-section .image-side img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 1.5rem;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
}
.cover-version-tag-section .content-side {
    padding-left: 2rem;
}
@media (max-width: 991px) {
    .cover-version-tag-section .content-side {
        padding-left: 0;
        text-align: center;
    }
}
.cover-version-tag-section .version-tag {
    display: inline-block;
    color: var(--color-primary, #FF6A00);
    font-size: 0.8rem;
    font-weight: 700;
    margin-bottom: 1rem;
    letter-spacing: 0.5px;
}
.cover-version-tag-section h1 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-version-tag-section p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
    margin-bottom: 2rem;
    max-width: 450px;
}
@media (max-width: 991px) {
    .cover-version-tag-section p {
       margin-left: auto;
       margin-right: auto;
    }
}
.cover-version-tag-section .btn-dark {
    display: inline-block;
    padding: 0.75rem 2rem;
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    border-radius: 0.5rem;
    font-weight: 600;
    text-decoration: none;
    transition: all 0.3s;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
