css:
.cover-nur-text-section {
    padding: 8rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-nur-text-section .container {
    width: 90%;
    max-width: 700px;
    margin: 0 auto;
    text-align: center;
}
.cover-nur-text-section h1 {
    font-size: clamp(2.5rem, 6vw, 4rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-nur-text-section .subtitle {
    font-size: 1.1rem;
    margin: 0 auto 2.5rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.cover-nur-text-section .btn-dark {
    display: inline-block;
    padding: 0.75rem 2rem;
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    border-radius: 0.5rem;
    font-weight: 600;
    text-decoration: none;
    transition: all 0.3s;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
