css:
.cover-online-kurs-section {
    padding: 2rem 0;
    background-color: #e5e7eb; /* gray-200 */
}
.cover-online-kurs-section .container {
    width: 90%;
    max-width: 1000px;
    margin: 0 auto;
}
.cover-online-kurs-section .main-card {
    background-color: var(--color-light, #FFFFFF);
    padding: 4rem 2rem;
    border-radius: 1.5rem;
    box-shadow: 0 20px 40px rgba(0,0,0,0.08);
}
.cover-online-kurs-section .content {
    max-width: 800px;
    margin: 0 auto;
}
.cover-online-kurs-section .version-tag {
    display: inline-block;
    background-color: #dbeafe; /* blue-100 */
    color: #3b82f6; /* blue-500 */
    padding: 0.25rem 0.75rem;
    border-radius: 9999px;
    font-size: 0.8rem;
    font-weight: 600;
    margin-bottom: 1rem;
}
.cover-online-kurs-section h1 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-online-kurs-section .subtitle {
    font-size: 1.1rem;
    max-width: 600px;
    margin: 0 auto 2.5rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.cover-online-kurs-section .btn-dark {
    display: inline-block;
    padding: 1rem 2.5rem;
    text-decoration: none;
    border-radius: 0.5rem;
    font-weight: 600;
    transition: all 0.3s ease;
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
.cover-online-kurs-section .rating-wrapper {
    margin-top: 3rem;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 1rem;
    color: var(--color-secondary, #6B7280);
    font-size: 0.9rem;
}
.cover-online-kurs-section .avatars {
    display: flex;
}
.cover-online-kurs-section .avatars img {
    width: 32px;
    height: 32px;
    border-radius: 50%;
    border: 2px solid var(--color-light, #FFFFFF);
    margin-left: -10px;
}
.cover-online-kurs-section .stars {
    color: #f59e0b; /* amber-500 */
}
.cover-online-kurs-section .video-placeholder {
    position: relative;
    margin-top: 3rem;
    border-radius: 1.5rem;
    overflow: hidden;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
    aspect-ratio: 16/9;
}
.cover-online-kurs-section .video-placeholder img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
}
.cover-online-kurs-section .play-button {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 70px;
    height: 70px;
    background-color: rgba(255,255,255,0.8);
    backdrop-filter: blur(5px);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    color: var(--color-dark, #1F2937);
    cursor: pointer;
    transition: transform 0.3s;
}
.cover-online-kurs-section .play-button:hover {
    transform: translate(-50%, -50%) scale(1.1);
}
.cover-online-kurs-section .play-button i { margin-left: 5px; }
.cover-online-kurs-section .logos-container-kurs {
    margin-top: 4rem;
    overflow: hidden;
    -webkit-mask-image: linear-gradient(to right, transparent, black 10%, black 90%, transparent);
    mask-image: linear-gradient(to right, transparent, black 10%, black 90%, transparent);
}
.cover-online-kurs-section .logo-scroller-track-kurs {
    display: flex;
    align-items: center;
    gap: 4rem;
    width: max-content;
    animation: scroll-kurs 40s linear infinite;
}
.cover-online-kurs-section .logos-container-kurs img {
    height: 28px;
    width: auto;
    filter: grayscale(100%);
    opacity: 0.6;
}
@keyframes scroll-kurs {
    from { transform: translateX(0); }
    to { transform: translateX(-50%); }
}
.cover-online-kurs-section .anim-delay-1 { transition-delay: 0.1s; }
.cover-online-kurs-section .anim-delay-2 { transition-delay: 0.2s; }
.cover-online-kurs-section .anim-delay-3 { transition-delay: 0.3s; }
.cover-online-kurs-section .anim-delay-4 { transition-delay: 0.4s; }
