css:
.cover-persoenlicher-touch-section {
    padding: 6rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-persoenlicher-touch-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.cover-persoenlicher-touch-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
}
@media (min-width: 992px) {
    .cover-persoenlicher-touch-container {
        grid-template-columns: 1fr 1fr;
    }
}
.cover-persoenlicher-touch-section .content-side {
    max-width: 450px;
}
.cover-persoenlicher-touch-section h1 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-persoenlicher-touch-section p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
    margin-bottom: 2rem;
}
.cover-persoenlicher-touch-section .btn-dark {
    display: inline-block;
    padding: 0.75rem 2rem;
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    border: 1px solid var(--color-dark, #1F2937);
    border-radius: 0.5rem;
    font-weight: 600;
    text-decoration: none;
    transition: all 0.3s;
}
.cover-persoenlicher-touch-section .video-placeholder {
    position: relative;
    width: 100%;
    aspect-ratio: 16 / 11;
    border-radius: 1.5rem;
    overflow: hidden;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
}
.cover-persoenlicher-touch-section .video-placeholder img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.cover-persoenlicher-touch-section .play-button-overlay { position: absolute; inset: 0; display: flex; align-items: center; justify-content: center; background-color: rgba(0,0,0,0.2); cursor: pointer; }
.cover-persoenlicher-touch-section .play-button-icon { width: 80px; height: 80px; background-color: rgba(255, 255, 255, 0.8); backdrop-filter: blur(5px); border-radius: 50%; display: flex; align-items: center; justify-content: center; transition: transform 0.3s; }
.cover-persoenlicher-touch-section .play-button-overlay:hover .play-button-icon { transform: scale(1.1); }
.cover-persoenlicher-touch-section .play-button-icon i { font-size: 1.5rem; color: var(--color-dark, #1F2937); margin-left: 5px; /* Optical adjustment */ }
