css:
.cover-2-section {
    background-color: #f8f9fa; /* A very light gray */
    padding: 6rem 0;
}
.cover-2-section .container { width: 90%; max-width: 800px; margin: 0 auto; padding: 0 1rem; text-align: center; }
.cover-2-section h1 {
    font-size: clamp(2.5rem, 6vw, 4rem);
    font-weight: 700;
    line-height: 1.2;
    margin-bottom: 1.5rem;
    color: var(--color-dark, #1F2937);
}
.cover-2-section .subtitle {
    font-size: 1.1rem;
    max-width: 600px;
    margin: 0 auto 2.5rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.cover-2-section .btn {
    display: inline-block;
    padding: 1rem 2.5rem;
    text-decoration: none;
    border-radius: 0.5rem;
    font-weight: 600;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
.cover-2-section .btn-dark {
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
}
.cover-2-section .btn-dark:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}
.cover-2-section .logos-container {
    max-width: 1000px;
    margin: 4rem auto 0;
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-wrap: wrap;
    gap: 2rem;
}
.cover-2-section .logos-container img {
    height: 24px;
    width: auto;
    filter: grayscale(100%);
    opacity: 0.6;
    transition: opacity 0.3s, filter 0.3s;
}
.cover-2-section .logos-container img:hover {
    opacity: 1;
    filter: grayscale(0%);
}
.cover-2-section .anim-delay-1 { transition-delay: 0.1s; }
.cover-2-section .anim-delay-2 { transition-delay: 0.2s; }
.cover-2-section .anim-delay-3 { transition-delay: 0.3s; }
