css:
.cover-problem-loesung-section {
    padding: 6rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-problem-loesung-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.cover-problem-loesung-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
}
@media (min-width: 992px) {
    .cover-problem-loesung-container {
        grid-template-columns: 1fr 1fr;
    }
}
.cover-problem-loesung-section h1 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-problem-loesung-section .content-side p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
    margin-bottom: 2rem;
}
.cover-problem-loesung-section .features-list {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    margin-bottom: 2.5rem;
}
.cover-problem-loesung-section .feature-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    color: var(--color-dark, #1F2937);
    font-weight: 500;
}
.cover-problem-loesung-section .feature-item i {
    color: var(--color-dark, #1F2937);
    font-size: 1.25rem;
}
.cover-problem-loesung-section .cta-group {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    gap: 1rem;
}
.cover-problem-loesung-section .btn {
    padding: 0.75rem 1.5rem;
    border-radius: 0.5rem;
    font-weight: 600;
    text-decoration: none;
    transition: all 0.3s;
}
.cover-problem-loesung-section .btn-dark {
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    border: 1px solid var(--color-dark, #1F2937);
}
.cover-problem-loesung-section .btn-light {
    background-color: transparent;
    color: var(--color-dark, #1F2937);
    border: 1px solid transparent;
}
.cover-problem-loesung-section .image-side img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 1.5rem;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
}
