css:
.cover-produkt-erlebnis-section {
    padding: 6rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-produkt-erlebnis-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
    padding: 0 1rem;
}
.cover-produkt-erlebnis-section .main-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
    margin-bottom: 4rem;
}
@media (min-width: 992px) {
    .cover-produkt-erlebnis-section .main-grid {
        grid-template-columns: 1fr 1fr;
    }
}
.cover-produkt-erlebnis-section .content-side h1 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-produkt-erlebnis-section .content-side p {
    font-size: 1.1rem;
    color: var(--color-secondary, #6B7280);
    margin-bottom: 2rem;
    line-height: 1.7;
}
.cover-produkt-erlebnis-section .btn-dark {
    display: inline-block;
    padding: 1rem 2.5rem;
    text-decoration: none;
    border-radius: 0.5rem;
    font-weight: 600;
    transition: all 0.3s ease;
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
.cover-produkt-erlebnis-section .image-slider-side {
    background-color: #e5e7eb; /* gray-200 */
    border-radius: 1.5rem;
    padding: 1.5rem;
}
.cover-produkt-erlebnis-section .slider-image-wrapper {
    aspect-ratio: 4/3;
    border-radius: 1rem;
    overflow: hidden;
    position: relative;
}
.cover-produkt-erlebnis-section .slider-image {
    width: 100%;
    height: 100%;
    object-fit: cover;
    position: absolute;
    top: 0;
    left: 0;
    opacity: 0;
    transition: opacity 0.5s ease-in-out;
}
.cover-produkt-erlebnis-section .slider-image.active {
    opacity: 1;
}
.cover-produkt-erlebnis-section .slider-nav {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 1rem;
    padding: 0 0.5rem;
}
.cover-produkt-erlebnis-section .arrow-btn {
    background: none;
    border: none;
    font-size: 1.5rem;
    color: var(--color-secondary, #6B7280);
    cursor: pointer;
}
.cover-produkt-erlebnis-section .dots {
    display: flex;
    gap: 0.5rem;
}
.cover-produkt-erlebnis-section .dot {
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background-color: #9ca3af; /* gray-400 */
    cursor: pointer;
    transition: background-color 0.3s;
}
.cover-produkt-erlebnis-section .dot.active {
    background-color: var(--color-dark, #1F2937);
}
.cover-produkt-erlebnis-section .features-grid-produkt {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1.5rem;
}
.cover-produkt-erlebnis-section .feature-card-produkt {
    background-color: #f3f4f6; /* gray-100 */
    padding: 1.5rem;
    border-radius: 1rem;
    font-size: 0.95rem;
    color: var(--color-secondary, #6B7280);
}
.cover-produkt-erlebnis-section .anim-delay-1 { transition-delay: 0.1s; }
.cover-produkt-erlebnis-section .anim-delay-2 { transition-delay: 0.2s; }
.cover-produkt-erlebnis-section .anim-delay-3 { transition-delay: 0.3s; }
