css:
.cover-produkt-merkmale-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-produkt-merkmale-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
}
.cover-produkt-merkmale-section .text-center { text-align: center; }
.cover-produkt-merkmale-section .header-part { margin-bottom: 4rem; }
.cover-produkt-merkmale-section h1 { font-size: clamp(2.5rem, 5vw, 3.5rem); font-weight: 700; color: var(--color-dark, #1F2937); margin-bottom: 1rem; }
.cover-produkt-merkmale-section .subtitle { color: var(--color-secondary, #6B7280); max-width: 700px; margin: 0 auto; }
.cover-produkt-merkmale-section .main-content-grid { display: grid; grid-template-columns: 1fr; gap: 3rem; align-items: center; margin-bottom: 4rem; }
@media (min-width: 992px) { .cover-produkt-merkmale-section .main-content-grid { grid-template-columns: 1fr 1fr; } }
.cover-produkt-merkmale-section .image-side img { width: 100%; height: 100%; object-fit: cover; border-radius: 1.5rem; box-shadow: 0 10px 30px rgba(0,0,0,0.1); }
.cover-produkt-merkmale-section .features-side { display: flex; flex-direction: column; gap: 2.5rem; }
.cover-produkt-merkmale-section .feature-block h3 { font-size: 1.5rem; font-weight: 600; color: var(--color-dark, #1F2937); margin-bottom: 0.75rem; }
.cover-produkt-merkmale-section .feature-block p { color: var(--color-secondary, #6B7280); line-height: 1.7; }
.cover-produkt-merkmale-section .cta-part { margin-top: 2rem; }
.cover-produkt-merkmale-section .btn-dark { display: inline-block; padding: 0.8rem 2.5rem; background-color: var(--color-dark, #1F2937); color: var(--color-light, #FFFFFF); border: none; border-radius: 0.5rem; font-weight: 600; text-decoration: none; }
