css:
.cover-5-section {
    background-color: #f8f9fa; /* A very light gray */
    padding: 6rem 0;
}
.cover-5-section .container { width: 90%; max-width: 900px; margin: 0 auto; padding: 0 1rem; }
.cover-5-section .hero-part { margin-bottom: 4rem; text-align: center; }
.cover-5-section h1 {
    font-size: clamp(2.5rem, 6vw, 4rem);
    font-weight: 700;
    line-height: 1.2;
    margin-bottom: 1.5rem;
    color: var(--color-dark, #1F2937);
}
.cover-5-section .subtitle {
    font-size: 1.1rem;
    max-width: 600px;
    margin: 0 auto 2.5rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.cover-5-section .btn {
    display: inline-block;
    padding: 1rem 2.5rem;
    text-decoration: none;
    border-radius: 0.5rem;
    font-weight: 600;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
.cover-5-section .btn-dark {
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
}
.cover-5-section .btn-dark:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}
.cover-5-section .features-part-5 {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
}
.cover-5-section .feature-card-5 {
    padding: 2rem;
    border-radius: 1rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.08);
}
.cover-5-section .dark-card {
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
}
.cover-5-section .dark-card h3 {
    font-size: 1.5rem;
    color: var(--color-light, #FFFFFF);
    margin-bottom: 1rem;
}
.cover-5-section .dark-card p {
    color: #d1d5db; /* gray-300 */
}
.cover-5-section .light-card {
    background-color: #e5e7eb; /* gray-200 */
    text-align: center;
    display: flex;
    flex-direction: column;
    justify-content: center;
}
.cover-5-section .light-card .stat-number {
    font-size: 3.5rem;
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    line-height: 1;
}
.cover-5-section .light-card p {
    color: var(--color-secondary, #6B7280);
    margin-top: 0.5rem;
}
.cover-5-section .anim-delay-1 { transition-delay: 0.1s; }
.cover-5-section .anim-delay-2 { transition-delay: 0.2s; }
.cover-5-section .anim-delay-3 { transition-delay: 0.3s; }
.cover-5-section .anim-delay-4 { transition-delay: 0.4s; }
