css:
.section-cover-split-diagonal-dark {
  background: #0a0a0a;
  color: white;
  padding: 100px 0;
  overflow: hidden;
  position: relative;
}
.section-cover-split-diagonal-dark::before {
  content: '';
  position: absolute;
  top: -50%;
  right: -20%;
  width: 80%;
  height: 200%;
  background: #1a1a1a;
  transform: rotate(-15deg);
  z-index: 0;
}
.section-cover-split-diagonal-dark .container {
  position: relative;
  z-index: 1;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}
.section-cover-split-diagonal-dark .grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 60px;
  align-items: center;
}
.section-cover-split-diagonal-dark h1 {
  font-family: var(--font-heading, serif);
  font-size: 3.5rem;
  line-height: 1.1;
  margin-bottom: 20px;
}
.section-cover-split-diagonal-dark p {
  color: #999;
  font-size: 1.1rem;
  margin-bottom: 30px;
  max-width: 450px;
}
.section-cover-split-diagonal-dark .btn-glow {
  display: inline-block;
  background: #ff4500;
  color: white;
  padding: 15px 35px;
  border-radius: 4px;
  text-decoration: none;
  font-weight: 600;
  box-shadow: 0 0 20px rgba(255, 69, 0, 0.4);
  transition: 0.3s;
}
.section-cover-split-diagonal-dark .btn-glow:hover {
  box-shadow: 0 0 30px rgba(255, 69, 0, 0.6);
  transform: translateY(-2px);
}
.section-cover-split-diagonal-dark .image-side {
  position: relative;
}
.section-cover-split-diagonal-dark .img-clipper {
  width: 100%;
  height: 500px;
  border-radius: 20px;
  overflow: hidden;
  transform: rotate(3deg);
  border: 2px solid #333;
}
.section-cover-split-diagonal-dark img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transform: rotate(-3deg) scale(1.1);
}
@media (max-width: 991px) {
  .section-cover-split-diagonal-dark .grid {
    grid-template-columns: 1fr;
    text-align: center;
  }
  .section-cover-split-diagonal-dark p {
    margin: 0 auto 30px;
  }
  .section-cover-split-diagonal-dark::before {
    width: 100%;
    right: 0;
    transform: rotate(0);
    top: 0;
    height: 50%;
  }
}
