css:
.cover-strategie-skalierung-section {
    padding: 4rem 0;
    background-color: var(--color-light, #FFFFFF);
    min-height: 80vh;
    display: flex;
    align-items: center;
}
.cover-strategie-skalierung-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
}
.cover-strategie-skalierung-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
}
@media (min-width: 992px) {
    .cover-strategie-skalierung-container {
        grid-template-columns: 45% 55%;
    }
    .cover-strategie-skalierung-section .image-side {
        order: 1;
    }
    .cover-strategie-skalierung-section .content-side {
        order: 0;
    }
}
.cover-strategie-skalierung-section .pre-headline {
    color: var(--color-secondary, #6B7280);
    font-size: 0.9rem;
    font-weight: 600;
    margin-bottom: 1rem;
}
.cover-strategie-skalierung-section h1 {
    font-size: clamp(2.5rem, 5vw, 3.25rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.cover-strategie-skalierung-section .description {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
    margin-bottom: 2.5rem;
}
.cover-strategie-skalierung-section .cta-group {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
    margin-bottom: 3rem;
}
.cover-strategie-skalierung-section .btn {
    padding: 0.75rem 1.5rem;
    border-radius: 0.5rem;
    font-weight: 600;
    text-decoration: none;
    transition: all 0.3s;
}
.cover-strategie-skalierung-section .btn-dark {
    background-color: var(--color-dark, #1F2937);
    color: var(--color-light, #FFFFFF);
    border: 1px solid var(--color-dark, #1F2937);
}
.cover-strategie-skalierung-section .btn-light {
    background-color: transparent;
    color: var(--color-dark, #1F2937);
    border: 1px solid #d1d5db; /* gray-300 */
}
.cover-strategie-skalierung-section .social-icons {
    display: flex;
    gap: 1.5rem;
}
.cover-strategie-skalierung-section .social-icons a {
    color: var(--color-secondary, #6B7280);
    font-size: 1.25rem;
    transition: color 0.3s;
}
.cover-strategie-skalierung-section .social-icons a:hover {
    color: var(--color-dark, #1F2937);
}
.cover-strategie-skalierung-section .image-side img {
    width: 100%;
    height: 100%;
    max-height: 600px;
    object-fit: cover;
    border-radius: 1.5rem;
}
