css:
.section-cover-testimonial-split-1 {
  padding: 4rem 1rem;
  background-color: var(--color-light);
  overflow: hidden;
  font-family: var(--font-body);
}
.section-cover-testimonial-split-1 .container {
  max-width: 1200px;
  margin: 0 auto;
}
.section-cover-testimonial-split-1 .header-row {
  display: flex;
  justify-content: space-between;
  align-items: flex-end;
  margin-bottom: 3rem;
  flex-wrap: wrap;
  gap: 1rem;
}
.section-cover-testimonial-split-1 .header-content {
  max-width: 600px;
}
.section-cover-testimonial-split-1 .eyebrow {
  display: block;
  font-size: 0.875rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: var(--color-secondary);
  margin-bottom: 0.5rem;
  font-weight: 600;
}
.section-cover-testimonial-split-1 .heading {
  font-family: var(--font-heading);
  font-size: 2.5rem;
  color: var(--color-dark);
  line-height: 1.2;
  margin: 0;
}
.section-cover-testimonial-split-1 .btn-primary {
  background-color: #a3e635; /* Lime Green match */
  color: #000;
  padding: 0.75rem 1.5rem;
  border-radius: 50px;
  text-decoration: none;
  font-weight: 600;
  transition: transform 0.2s;
}
.section-cover-testimonial-split-1 .btn-primary:hover {
  transform: translateY(-2px);
}
.section-cover-testimonial-split-1 .content-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
}
.section-cover-testimonial-split-1 .cards-column {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}
.section-cover-testimonial-split-1 .review-card {
  background: var(--color-surface-1);
  border-radius: 12px;
  padding: 1.5rem;
  box-shadow: 0 4px 20px rgba(0,0,0,0.05);
  transition: transform 0.3s ease;
}
.section-cover-testimonial-split-1 .review-card:hover {
  transform: translateX(10px);
}
.section-cover-testimonial-split-1 .review-card.highlight {
  background: linear-gradient(135deg, #1a1a1a 0%, #333 100%);
  color: #fff;
}
.section-cover-testimonial-split-1 .card-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 1rem;
}
.section-cover-testimonial-split-1 .user-info {
  display: flex;
  gap: 1rem;
  align-items: center;
}
.section-cover-testimonial-split-1 .avatar-circle {
  width: 50px;
  height: 50px;
  border-radius: 50%;
  overflow: hidden;
  background: #ddd;
}
.section-cover-testimonial-split-1 .avatar-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}
.section-cover-testimonial-split-1 .user-meta h4 {
  margin: 0;
  font-size: 1rem;
  font-weight: 700;
  color: inherit;
}
.section-cover-testimonial-split-1 .review-card.highlight .user-meta h4 {
  color: #fff;
}
.section-cover-testimonial-split-1 .user-meta span {
  font-size: 0.8rem;
  opacity: 0.7;
}
.section-cover-testimonial-split-1 .quote-icon {
  font-size: 3rem;
  line-height: 1;
  font-family: serif;
  opacity: 0.3;
}
.section-cover-testimonial-split-1 .review-text {
  font-size: 0.95rem;
  line-height: 1.5;
  margin: 0;
  opacity: 0.9;
}
.section-cover-testimonial-split-1 .image-column {
  position: relative;
  display: flex;
  justify-content: center;
}
.section-cover-testimonial-split-1 .image-wrapper {
  position: relative;
  width: 100%;
  max-width: 450px;
  aspect-ratio: 1;
}
.section-cover-testimonial-split-1 .circle-bg {
  position: absolute;
  top: 10%;
  left: 10%;
  width: 80%;
  height: 80%;
  background-color: #333;
  border-radius: 50%;
  z-index: 0;
  overflow: hidden;
}
.section-cover-testimonial-split-1 .circle-bg::after {
  content: '';
  position: absolute;
  inset: 0;
  background: url('https://www.transparenttextures.com/patterns/carbon-fibre.png');
  opacity: 0.2;
}
.section-cover-testimonial-split-1 .main-hero-img {
  position: relative;
  z-index: 1;
  width: 100%;
  height: 100%;
  object-fit: contain;
  border-bottom-left-radius: 50%;
  border-bottom-right-radius: 50%;
}
.section-cover-testimonial-split-1 .floating-icon {
  position: absolute;
  z-index: 2;
  font-size: 3rem;
  filter: drop-shadow(0 10px 15px rgba(0,0,0,0.2));
  animation: float 3s ease-in-out infinite;
}
.section-cover-testimonial-split-1 .icon-1 {
  bottom: 20%;
  left: -10%;
  font-size: 4rem;
}
.section-cover-testimonial-split-1 .icon-2 {
  top: 30%;
  right: 0;
}
@keyframes float {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
}
@media (max-width: 992px) {
  .section-cover-testimonial-split-1 .content-grid {
    grid-template-columns: 1fr;
    gap: 3rem;
  }
  .section-cover-testimonial-split-1 .header-row {
    flex-direction: column;
    align-items: flex-start;
  }
}

js:
(() => {
  const section = document.querySelector('.section-cover-testimonial-split-1');
  if (!section) return;

  const fetchImage = async (keyword, imgElement) => {
    try {
      const response = await fetch(`https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=${encodeURIComponent(keyword)}&image_type=photo&orientation=horizontal`);
      const data = await response.json();
      if (data.hits && data.hits.length > 0) {
        imgElement.src = data.hits[0].webformatURL;
      }
    } catch (err) {
      console.error('Error fetching image:', err);
    }
  };

  // Fetch avatar images
  const avatars = section.querySelectorAll('.avatar-img');
  avatars.forEach((img, index) => {
    const keyword = img.getAttribute('data-keyword') || 'person portrait';
    // Add slight variation to query to get different images if possible, though API might return same for exact query
    fetchImage(keyword + (index % 2 === 0 ? ' smile' : ' professional'), img);
  });

  // Fetch main hero image
  const mainHero = section.querySelector('.main-hero-img');
  if (mainHero) {
    fetchImage(mainHero.getAttribute('data-keyword'), mainHero);
  }
})();
