css:
.cover-video-kurs-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-video-kurs-section .container {
    width: 90%;
    max-width: 1000px;
    margin: 0 auto;
}
.cover-video-kurs-section .text-center { text-align: center; }
.cover-video-kurs-section .header-part {
    margin-bottom: 4rem;
}
.cover-video-kurs-section h1 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1rem;
}
.cover-video-kurs-section .subtitle {
    font-size: 1.1rem;
    max-width: 700px;
    margin: 0 auto;
    color: var(--color-secondary, #6B7280);
}
.cover-video-kurs-section .main-content-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    align-items: start;
}
@media (min-width: 992px) {
    .cover-video-kurs-section .main-content-grid {
        grid-template-columns: 1.5fr 1fr;
        gap: 3rem;
    }
}
.cover-video-kurs-section .video-placeholder { position: relative; width: 100%; aspect-ratio: 16 / 10; border-radius: 1rem; overflow: hidden; box-shadow: 0 10px 25px rgba(0,0,0,0.1); }
.cover-video-kurs-section .video-placeholder img { width: 100%; height: 100%; object-fit: cover; }
.cover-video-kurs-section .play-button-overlay { position: absolute; inset: 0; display: flex; align-items: center; justify-content: center; background-color: rgba(0,0,0,0.2); }
.cover-video-kurs-section .play-button-icon { width: 70px; height: 70px; background-color: rgba(255, 255, 255, 0.8); backdrop-filter: blur(5px); border-radius: 50%; display: flex; align-items: center; justify-content: center; font-size: 1.5rem; color: var(--color-dark, #1F2937); cursor: pointer; transition: transform 0.3s; }
.cover-video-kurs-section .play-button-overlay:hover .play-button-icon { transform: scale(1.1); }
.cover-video-kurs-section .play-button-icon i { margin-left: 5px; }
.cover-video-kurs-section .left-col-text { font-size: 0.9rem; color: var(--color-secondary, #6B7280); margin: 1.5rem 0; }
.cover-video-kurs-section .rating-wrapper { display: flex; flex-wrap: wrap; align-items: center; gap: 0.75rem; font-size: 0.9rem; color: var(--color-secondary, #6B7280); }
.cover-video-kurs-section .avatars { display: flex; }
.cover-video-kurs-section .avatars img { width: 32px; height: 32px; border-radius: 50%; border: 2px solid var(--color-light, #FFFFFF); margin-left: -10px; }
.cover-video-kurs-section .stars { color: #f59e0b; }
.cover-video-kurs-section .course-card {
    background-color: #f3f4f6; /* gray-100 */
    border-radius: 1rem;
    padding: 2.5rem;
    height: 100%;
}
.cover-video-kurs-section .course-card h2 { font-size: 1.75rem; color: var(--color-dark, #1F2937); margin-bottom: 0.5rem; }
.cover-video-kurs-section .course-card p { font-size: 0.95rem; color: var(--color-secondary, #6B7280); margin-bottom: 1.5rem; }
.cover-video-kurs-section .price { font-size: 3rem; font-weight: 700; color: var(--color-dark, #1F2937); }
.cover-video-kurs-section .payment-info { font-size: 0.9rem; color: var(--color-secondary, #6B7280); margin-top: -1rem; margin-bottom: 2rem; }
.cover-video-kurs-section .features-list { list-style: none; padding: 0; margin-bottom: 2rem; }
.cover-video-kurs-section .features-list li { display: flex; align-items: center; gap: 0.75rem; color: var(--color-dark, #1F2937); font-weight: 500; margin-bottom: 1rem; }
.cover-video-kurs-section .features-list li i { color: var(--color-secondary, #6B7280); }
.cover-video-kurs-section .btn-dark { display: block; width: 100%; padding: 0.75rem 1.5rem; background-color: var(--color-dark, #1F2937); color: var(--color-light, #FFFFFF); border: none; border-radius: 0.5rem; font-weight: 600; cursor: pointer; text-decoration: none; transition: background-color 0.3s; }
