css:
.cover-video-checkliste-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-video-checkliste-section .container { width: 90%; max-width: 1000px; margin: 0 auto; }
.cover-video-checkliste-section .text-center { text-align: center; }
.cover-video-checkliste-section .header-part { margin-bottom: 4rem; }
.cover-video-checkliste-section h1 { font-size: clamp(2.5rem, 5vw, 3.5rem); font-weight: 700; color: var(--color-dark, #1F2937); margin-bottom: 1rem; }
.cover-video-checkliste-section .subtitle { font-size: 1.1rem; max-width: 700px; margin: 0 auto; color: var(--color-secondary, #6B7280); }
.cover-video-checkliste-section .main-content-grid { display: grid; grid-template-columns: 1fr; gap: 3rem; align-items: center; margin-bottom: 4rem; }
@media (min-width: 992px) { .cover-video-checkliste-section .main-content-grid { grid-template-columns: 1fr 1fr; } }
.cover-video-checkliste-section .video-placeholder { position: relative; width: 100%; aspect-ratio: 16 / 10; border-radius: 1rem; overflow: hidden; box-shadow: 0 10px 25px rgba(0,0,0,0.1); }
.cover-video-checkliste-section .video-placeholder img { width: 100%; height: 100%; object-fit: cover; }
.cover-video-checkliste-section .play-button-overlay { position: absolute; inset: 0; display: flex; align-items: center; justify-content: center; background-color: rgba(0,0,0,0.2); }
.cover-video-checkliste-section .play-button-icon { width: 70px; height: 70px; background-color: rgba(255, 255, 255, 0.8); backdrop-filter: blur(5px); border-radius: 50%; display: flex; align-items: center; justify-content: center; font-size: 1.5rem; color: var(--color-dark, #1F2937); cursor: pointer; transition: transform 0.3s; }
.cover-video-checkliste-section .play-button-overlay:hover .play-button-icon { transform: scale(1.1); }
.cover-video-checkliste-section .play-button-icon i { margin-left: 5px; }
.cover-video-checkliste-section .checklist-side h3 { font-size: 1.75rem; font-weight: 600; color: var(--color-dark, #1F2937); margin-bottom: 1rem; }
.cover-video-checkliste-section .checklist-side p { color: var(--color-secondary, #6B7280); margin-bottom: 2rem; }
.cover-video-checkliste-section .checklist { list-style: none; padding: 0; display: flex; flex-direction: column; gap: 1rem; }
.cover-video-checkliste-section .checklist li { display: flex; align-items: flex-start; gap: 0.75rem; color: var(--color-dark, #1F2937); }
.cover-video-checkliste-section .checklist li i { color: var(--color-dark, #1F2937); margin-top: 4px; }
.cover-video-checkliste-section .cta-part { margin-top: 2rem; }
.cover-video-checkliste-section .btn-dark { display: inline-block; padding: 0.8rem 2.5rem; background-color: var(--color-dark, #1F2937); color: var(--color-light, #FFFFFF); border: none; border-radius: 0.5rem; font-weight: 600; text-decoration: none; }
