css:
.cover-webinar-anmeldung-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-webinar-anmeldung-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.webinar-anmeldung-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    align-items: center;
}
@media (min-width: 992px) {
    .webinar-anmeldung-container {
        grid-template-columns: 1.2fr 1fr;
        gap: 4rem;
    }
}
.cover-webinar-anmeldung-section .video-placeholder { position: relative; width: 100%; aspect-ratio: 16 / 10; border-radius: 1.5rem; overflow: hidden; box-shadow: 0 10px 30px rgba(0,0,0,0.1); margin-bottom: 2rem; }
.cover-webinar-anmeldung-section .video-placeholder img { width: 100%; height: 100%; object-fit: cover; }
.cover-webinar-anmeldung-section .play-button-overlay { position: absolute; inset: 0; display: flex; align-items: center; justify-content: center; background-color: rgba(0,0,0,0.2); }
.cover-webinar-anmeldung-section .play-button-icon { width: 80px; height: 80px; background-color: rgba(255, 255, 255, 0.8); backdrop-filter: blur(5px); border-radius: 50%; display: flex; align-items: center; justify-content: center; font-size: 1.5rem; color: var(--color-dark, #1F2937); cursor: pointer; transition: transform 0.3s; }
.cover-webinar-anmeldung-section .author-info { display: flex; align-items: center; gap: 1rem; }
.cover-webinar-anmeldung-section .author-avatars { display: flex; }
.cover-webinar-anmeldung-section .author-avatars img { width: 50px; height: 50px; border-radius: 50%; border: 3px solid var(--color-light, #FFFFFF); margin-left: -15px; }
.cover-webinar-anmeldung-section .author-info h4 { font-size: 1rem; font-weight: 600; margin: 0; }
.cover-webinar-anmeldung-section .author-info p { font-size: 0.9rem; color: var(--color-secondary, #6B7280); margin: 0; }
.cover-webinar-anmeldung-section .form-card { background-color: #f9fafb; border-radius: 1.5rem; padding: 2.5rem; }
.cover-webinar-anmeldung-section .form-card h2 { font-size: 2rem; font-weight: 700; color: var(--color-dark, #1F2937); margin-bottom: 1rem; }
.cover-webinar-anmeldung-section .form-card p { color: var(--color-secondary, #6B7280); margin-bottom: 2rem; }
.cover-webinar-anmeldung-section form { display: flex; flex-direction: column; gap: 1rem; }
.cover-webinar-anmeldung-section .form-row { display: grid; grid-template-columns: 1fr 1fr; gap: 1rem; }
.cover-webinar-anmeldung-section form input { width: 100%; padding: 0.75rem 1rem; background-color: var(--color-light, #FFFFFF); border: 1px solid #d1d5db; border-radius: 0.5rem; font-size: 1rem; }
.cover-webinar-anmeldung-section .btn-dark { padding: 0.8rem 1.5rem; background-color: var(--color-dark, #1F2937); color: var(--color-light, #FFFFFF); border: none; border-radius: 0.5rem; font-weight: 600; cursor: pointer; }
