css:
.cover-webinar-countdown-section {
    padding: 6rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.cover-webinar-countdown-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.countdown-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
}
@media (min-width: 992px) {
    .countdown-container {
        grid-template-columns: 1fr 1fr;
    }
}
.cover-webinar-countdown-section .content-side .tag { display: inline-block; background-color: #e5e7eb; padding: 0.25rem 0.75rem; border-radius: 9999px; font-size: 0.8rem; font-weight: 600; margin-bottom: 1.5rem; }
.cover-webinar-countdown-section h1 { font-size: clamp(2.5rem, 5vw, 3.5rem); font-weight: 700; line-height: 1.2; color: var(--color-dark, #1F2937); margin-bottom: 1.5rem; }
.cover-webinar-countdown-section .content-side p { color: var(--color-secondary, #6B7280); line-height: 1.7; max-width: 500px; }
.cover-webinar-countdown-section .form-side { background-color: #f3f4f6; border-radius: 1rem; padding: 2.5rem; box-shadow: 0 10px 25px rgba(0,0,0,0.08); }
.cover-webinar-countdown-section .countdown-timer { display: flex; justify-content: center; gap: 1.5rem; margin-bottom: 2rem; }
.cover-webinar-countdown-section .time-block { text-align: center; }
.cover-webinar-countdown-section .time-block span { font-size: 3rem; font-weight: 700; color: var(--color-dark, #1F2937); display: block; }
.cover-webinar-countdown-section .time-block small { font-size: 0.8rem; color: var(--color-secondary, #6B7280); }
.cover-webinar-countdown-section .webinar-form { display: flex; flex-direction: column; gap: 1rem; }
.cover-webinar-countdown-section .form-row { display: grid; grid-template-columns: 1fr 1fr; gap: 1rem; }
.cover-webinar-countdown-section .webinar-form input { width: 100%; padding: 0.75rem 1rem; background-color: var(--color-light, #FFFFFF); border: 1px solid #d1d5db; border-radius: 0.5rem; font-size: 1rem; }
.cover-webinar-countdown-section .btn-dark { padding: 0.8rem 1.5rem; background-color: var(--color-dark, #1F2937); color: var(--color-light, #FFFFFF); border: none; border-radius: 0.5rem; font-weight: 600; cursor: pointer; }
.cover-webinar-countdown-section .scarcity-text { text-align: center; font-size: 0.9rem; color: var(--color-secondary, #6B7280); margin-top: 1.5rem; }
