css:
.cover-section-creative {
  font-family: 'Montserrat', sans-serif;
  background-color: #f4f4f4;
  padding: 70px 20px;
}
.cover-section-creative .container {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 50px;
}
.cover-section-creative .content {
  flex: 1;
  text-align: left;
  max-width: 500px;
}
.cover-section-creative .title {
  font-size: 44px;
  font-weight: 700;
  color: #333333;
  margin-bottom: 18px;
  line-height: 1.25;
}
.cover-section-creative .subtitle {
  font-size: 18px;
  color: #555555;
  margin-bottom: 28px;
  line-height: 1.6;
}
.cover-section-creative .features {
  list-style: none;
  padding: 0;
  margin-bottom: 28px;
}
.cover-section-creative .features li {
  display: flex;
  align-items: center;
  margin-bottom: 12px;
  font-size: 16px;
  color: #444444;
}
.cover-section-creative .features .icon {
  color: #ff6b6b;
  margin-right: 10px;
  font-weight: bold;
}
.cover-section-creative .actions {
  display: flex;
  gap: 15px;
  flex-wrap: wrap;
}
.cover-section-creative .btn-primary {
  background-color: #4ecdc4;
  color: #ffffff;
  padding: 13px 28px;
  border: none;
  border-radius: 6px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s ease;
}
.cover-section-creative .btn-primary:hover {
  background-color: #3b9d96;
}
.cover-section-creative .btn-secondary {
  background-color: transparent;
  color: #4ecdc4;
  padding: 13px 28px;
  border: 2px solid #4ecdc4;
  border-radius: 6px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cover-section-creative .btn-secondary:hover {
  background-color: #4ecdc4;
  color: #ffffff;
}
.cover-section-creative .image-container {
  flex: 1;
  max-width: 600px;
}
.cover-section-creative .image-container img {
  width: 100%;
  height: auto;
  display: block;
  border-radius: 10px;
  box-shadow: 0 6px 20px rgba(0, 0, 0, 0.15);
}

@media (min-width: 768px) {
  .cover-section-creative .container {
    flex-direction: row;
    justify-content: space-between;
  }
  .cover-section-creative .content {
    text-align: left;
    flex: 0 0 50%;
    max-width: 500px;
  }
  .cover-section-creative .image-container {
    flex: 0 0 45%;
    max-width: 550px;
  }
  .cover-section-creative .title {
    font-size: 50px;
  }
  .cover-section-creative .subtitle {
    font-size: 19px;
  }
}

@media (min-width: 992px) {
  .cover-section-creative .title {
    font-size: 58px;
  }
  .cover-section-creative .subtitle {
    font-size: 20px;
  }
}

js:
window.addEventListener('load', function() {
  const images = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  images.forEach(img => {
    fetch(img.src)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].largeImageURL;
        } else {
          img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
        }
      })
      .catch(error => {
        console.error('Error fetching Pixabay image:', error);
        img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
      });
  });
});
