css:
.dark-hero-3 {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 90vh;
  padding: 40px 20px;
  color: #FFFFFF;
  text-align: center;
  overflow: hidden;
  font-family: system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
}
.dark-hero-background-3 {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
}
.dark-hero-img-3 {
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
}
.dark-hero-overlay-3 {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(10, 10, 20, 0.7);
}
.dark-hero-content-3 {
  position: relative;
  z-index: 2;
  max-width: 750px;
}
.dark-hero-heading-3 {
  font-size: 2.8rem;
  font-weight: 800;
  margin-bottom: 16px;
  letter-spacing: -1px;
}
.dark-hero-subheading-3 {
  font-size: 1.15rem;
  line-height: 1.6;
  margin-bottom: 32px;
  color: #E0E0E0;
}
.dark-hero-cta-3 {
  background-color: transparent;
  color: #FFFFFF;
  padding: 12px 30px;
  border: 2px solid #FFFFFF;
  border-radius: 5px;
  text-decoration: none;
  font-weight: 600;
  transition: background-color 0.3s ease, color 0.3s ease;
  display: inline-block;
}
.dark-hero-cta-3:hover {
  background-color: #FFFFFF;
  color: #0A0A14;
}
@media (min-width: 768px) {
  .dark-hero-heading-3 {
    font-size: 4rem;
  }
  .dark-hero-subheading-3 {
    font-size: 1.3rem;
  }
}
@media (min-width: 1024px) {
  .dark-hero-heading-3 {
    font-size: 5rem;
  }
}
