css:
.cover-section-luxury {
  font-family: 'Playfair Display', serif;
  background-color: #fdfdfd;
  padding: 80px 25px;
}
.cover-section-luxury .container {
  max-width: 1280px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 50px;
}
.cover-section-luxury .content {
  flex: 1;
  text-align: left;
  max-width: 550px;
}
.cover-section-luxury .title {
  font-size: 54px;
  font-weight: 700;
  color: #2c3e50;
  margin-bottom: 22px;
  line-height: 1.2;
}
.cover-section-luxury .subtitle {
  font-size: 20px;
  color: #7f8c8d;
  margin-bottom: 40px;
  line-height: 1.7;
}
.cover-section-luxury .actions {
  display: flex;
  gap: 18px;
  flex-wrap: wrap;
}
.cover-section-luxury .btn-primary {
  background-color: #b8860b;
  color: #ffffff;
  padding: 14px 30px;
  border: none;
  border-radius: 3px;
  font-size: 17px;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.3s ease;
}
.cover-section-luxury .btn-primary:hover {
  background-color: #996b0a;
}
.cover-section-luxury .btn-secondary {
  background-color: transparent;
  color: #b8860b;
  padding: 14px 30px;
  border: 1px solid #b8860b;
  border-radius: 3px;
  font-size: 17px;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cover-section-luxury .btn-secondary:hover {
  background-color: #b8860b;
  color: #ffffff;
}
.cover-section-luxury .image-container {
  flex: 1;
  max-width: 650px;
}
.cover-section-luxury .image-container img {
  width: 100%;
  height: auto;
  display: block;
  border-radius: 12px;
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.18);
}

@media (min-width: 768px) {
  .cover-section-luxury .container {
    flex-direction: row;
    justify-content: space-between;
  }
  .cover-section-luxury .content {
    text-align: left;
    flex: 0 0 50%;
    max-width: 520px;
  }
  .cover-section-luxury .image-container {
    flex: 0 0 45%;
    max-width: 580px;
  }
  .cover-section-luxury .title {
    font-size: 60px;
  }
  .cover-section-luxury .subtitle {
    font-size: 22px;
  }
}

@media (min-width: 992px) {
  .cover-section-luxury .title {
    font-size: 70px;
  }
  .cover-section-luxury .subtitle {
    font-size: 24px;
  }
}

