css:
.cover-section-futuristic {
  font-family: 'Orbitron', sans-serif;
  background-color: #0a0a1a;
  color: #e0e0e0;
  padding: 80px 20px;
}
.cover-section-futuristic .container {
  max-width: 1280px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 50px;
}
.cover-section-futuristic .content {
  flex: 1;
  text-align: center;
  max-width: 550px;
}
.cover-section-futuristic .title {
  font-size: 50px;
  font-weight: 700;
  color: #ffffff;
  margin-bottom: 18px;
  line-height: 1.2;
}
.cover-section-futuristic .subtitle {
  font-size: 20px;
  color: #b0b0d0;
  margin-bottom: 35px;
  line-height: 1.6;
}
.cover-section-futuristic .actions {
  display: flex;
  justify-content: center;
  gap: 15px;
  flex-wrap: wrap;
}
.cover-section-futuristic .btn-primary {
  background-color: #00bcd4;
  color: #0a0a1a;
  padding: 14px 30px;
  border: none;
  border-radius: 5px;
  font-size: 17px;
  font-weight: 700;
  cursor: pointer;
  transition: background-color 0.3s ease;
}
.cover-section-futuristic .btn-primary:hover {
  background-color: #00acc1;
}
.cover-section-futuristic .btn-secondary {
  background-color: transparent;
  color: #00bcd4;
  padding: 14px 30px;
  border: 1px solid #00bcd4;
  border-radius: 5px;
  font-size: 17px;
  font-weight: 700;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cover-section-futuristic .btn-secondary:hover {
  background-color: #00bcd4;
  color: #0a0a1a;
}
.cover-section-futuristic .image-container {
  flex: 1;
  width: 100%;
  max-width: 650px;
}
.cover-section-futuristic .image-container img {
  width: 100%;
  height: auto;
  display: block;
  border-radius: 10px;
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.25);
}

@media (min-width: 768px) {
  .cover-section-futuristic .container {
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
  }
  .cover-section-futuristic .content {
    text-align: left;
    flex: 0 0 50%;
    max-width: 500px;
  }
  .cover-section-futuristic .image-container {
    flex: 0 0 45%;
    max-width: 580px;
  }
  .cover-section-futuristic .title {
    font-size: 56px;
  }
  .cover-section-futuristic .subtitle {
    font-size: 21px;
  }
}

@media (min-width: 992px) {
  .cover-section-futuristic .title {
    font-size: 64px;
  }
  .cover-section-futuristic .subtitle {
    font-size: 22px;
  }
}

js:
window.addEventListener('load', function() {
  const images = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  images.forEach(img => {
    fetch(img.src)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].largeImageURL;
        } else {
          img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
        }
      })
      .catch(error => {
        console.error('Error fetching Pixabay image:', error);
        img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
      });
  });
});
