css:
.gradient-hero-2 {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 60px 20px;
  min-height: 70vh;
  text-align: center;
  color: #FFFFFF;
  font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
  background: linear-gradient(135deg, #667eea, #764ba2, #23a6d5, #23d5ab);
  background-size: 400% 400%;
  animation: gradient-animation-2 15s ease infinite;
}
@keyframes gradient-animation-2 {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}
.gradient-hero-content-2 {
  max-width: 800px;
}
.gradient-hero-heading-2 {
  font-size: 3rem;
  font-weight: 700;
  margin-bottom: 20px;
  text-shadow: 0 2px 4px rgba(0,0,0,0.2);
}
.gradient-hero-subheading-2 {
  font-size: 1.2rem;
  line-height: 1.7;
  margin-bottom: 40px;
  opacity: 0.9;
}
.gradient-hero-cta-2 {
  background-color: #FFFFFF;
  color: #667eea;
  padding: 15px 35px;
  border-radius: 50px;
  text-decoration: none;
  font-weight: bold;
  font-size: 1rem;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(0,0,0,0.1);
  display: inline-block;
}
.gradient-hero-cta-2:hover {
  background-color: #f0f0f0;
  color: #764ba2;
  transform: scale(1.05);
  box-shadow: 0 6px 20px rgba(0,0,0,0.15);
}
@media (min-width: 768px) {
  .gradient-hero-heading-2 {
    font-size: 4.5rem;
  }
  .gradient-hero-subheading-2 {
    font-size: 1.4rem;
  }
  .gradient-hero-2 {
    min-height: 80vh;
  }
}
