.section-hero-interactive-tabs-a1b2 .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 4rem 1rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}

.section-hero-interactive-tabs-a1b2 .hero-content {
  margin-bottom: 3rem;
  max-width: 700px;
}

.section-hero-interactive-tabs-a1b2 .hero-title {
  font-size: clamp(2.5rem, 6vw, 4rem);
  margin-bottom: 1rem;
  color: var(--color-dark);
  font-family: var(--font-heading);
}

.section-hero-interactive-tabs-a1b2 .hero-subtitle {
  font-size: clamp(1rem, 2.5vw, 1.25rem);
  color: var(--color-secondary);
  margin-bottom: 2rem;
  font-family: var(--font-body);
}

.section-hero-interactive-tabs-a1b2 .cta-button {
  background-color: var(--color-primary);
  color: var(--color-light);
  padding: 0.75rem 2rem;
  border: none;
  border-radius: 5px;
  font-size: 1rem;
  cursor: pointer;
  transition: background-color 0.3s ease;
  font-family: var(--font-body);
}

.section-hero-interactive-tabs-a1b2 .cta-button:hover {
  background-color: var(--color-secondary);
}

.section-hero-interactive-tabs-a1b2 .tabs-container {
  width: 100%;
  margin-top: 3rem;
}

.section-hero-interactive-tabs-a1b2 .tab-buttons {
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-bottom: 2rem;
  flex-wrap: wrap;
}

.section-hero-interactive-tabs-a1b2 .tab-button {
  background: none;
  border: none;
  padding: 0.75rem 1.5rem;
  cursor: pointer;
  font-size: 1.1rem;
  font-family: var(--font-body);
  color: var(--color-secondary);
  border-bottom: 2px solid transparent;
  transition: color 0.3s ease, border-color 0.3s ease;
}

.section-hero-interactive-tabs-a1b2 .tab-button.active {
  color: var(--color-primary);
  border-bottom-color: var(--color-primary);
  font-weight: bold;
}

.section-hero-interactive-tabs-a1b2 .tab-content {
  position: relative;
  min-height: 250px;
}

.section-hero-interactive-tabs-a1b2 .tab-pane {
  display: none;
  text-align: center;
  opacity: 0;
  transition: opacity 0.5s ease-in-out;
  animation: fadeIn 0.5s ease-in-out;
}

.section-hero-interactive-tabs-a1b2 .tab-pane.active {
  display: block;
  opacity: 1;
}

.section-hero-interactive-tabs-a1b2 .tab-pane p {
  font-size: 1rem;
  color: var(--color-secondary);
  margin-bottom: 1.5rem;
  font-family: var(--font-body);
}

.section-hero-interactive-tabs-a1b2 .tab-image {
  max-width: 100%;
  height: auto;
  border-radius: 8px;
  margin: 0 auto;
  display: block;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@media (min-width: 768px) {
  .section-hero-interactive-tabs-a1b2 .container {
    flex-direction: row;
    text-align: left;
    gap: 3rem;
  }
  .section-hero-interactive-tabs-a1b2 .hero-content {
    flex: 1;
    margin-bottom: 0;
  }
  .section-hero-interactive-tabs-a1b2 .tabs-container {
    flex: 1;
    margin-top: 0;
  }
  .section-hero-interactive-tabs-a1b2 .tab-buttons {
    justify-content: flex-start;
  }
}

