(() => {
  const section = document.currentScript.closest('section');
  if (!section) return;

  const tabButtons = section.querySelectorAll('.tab-button');
  const tabPanes = section.querySelectorAll('.tab-pane');

  tabButtons.forEach(button => {
    button.addEventListener('click', () => {
      const targetTab = button.getAttribute('data-tab');

      // Update active button state
      tabButtons.forEach(btn => btn.classList.remove('active'));
      button.classList.add('active');

      // Show corresponding tab pane
      tabPanes.forEach(pane => {
        pane.classList.remove('active');
        pane.setAttribute('aria-selected', 'false');
      });
      const activePane = section.querySelector(`#${targetTab}`);
      if (activePane) {
        activePane.classList.add('active');
        activePane.setAttribute('aria-selected', 'true');
      }
    });
  });

  // Image fetching logic
  const images = section.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  images.forEach(img => {
    const apiUrl = img.getAttribute('src');
    fetch(apiUrl)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].webformatURL;
          img.alt = data.hits[0].tags || 'AI Generated Image';
        } else {
          img.alt = 'Image not found';
          // Consider a fallback image or styling here
          console.warn('No image found from Pixabay API for:', apiUrl);
        }
      })
      .catch(error => {
        console.error('Error fetching image from Pixabay:', error);
        img.alt = 'Image loading error';
        // Consider a fallback image or styling here
      });
  });
})();
