.section-hero-soft-pastel-x2 {
  --bg-gradient: linear-gradient(180deg, #e9f5f5 0%, #e6f0ff 100%);
  --primary-dark: #2e3546;
  --accent-blue: #5c9aff;
  --text-main: #2d3748;
  --text-muted: #718096;

  background: var(--bg-gradient);
  padding: 8rem 2rem 6rem;
  font-family: 'Inter', sans-serif;
  position: relative;
  overflow: hidden;
}

.section-hero-soft-pastel-x2 .container {
  max-width: 1200px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr;
  gap: 4rem;
  align-items: center;
}

@media (min-width: 992px) {
  .section-hero-soft-pastel-x2 .container {
    grid-template-columns: 1fr 1fr;
  }
}

.section-hero-soft-pastel-x2 .badge-pill {
  display: inline-block;
  background: rgba(92, 154, 255, 0.1);
  color: var(--accent-blue);
  padding: 0.5rem 1rem;
  border-radius: 100px;
  font-size: 0.875rem;
  font-weight: 600;
  margin-bottom: 1.5rem;
}

.section-hero-soft-pastel-x2 .hero-title {
  font-size: clamp(2.5rem, 5vw, 4rem);
  line-height: 1.1;
  color: var(--primary-dark);
  margin-bottom: 1.5rem;
  letter-spacing: -0.02em;
}

.section-hero-soft-pastel-x2 .gradient-text {
  background: linear-gradient(135deg, #5c9aff, #3bcdb0);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.section-hero-soft-pastel-x2 .hero-desc {
  font-size: 1.125rem;
  color: var(--text-muted);
  line-height: 1.6;
  margin-bottom: 2.5rem;
  max-width: 90%;
}

.section-hero-soft-pastel-x2 .cta-group {
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
}

.section-hero-soft-pastel-x2 .btn-primary {
  background-color: var(--primary-dark);
  color: #fff;
  border: none;
  padding: 0.875rem 2rem;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  transition: transform 0.2s;
}

.section-hero-soft-pastel-x2 .btn-secondary {
  background-color: transparent;
  color: var(--primary-dark);
  border: 1px solid rgba(46, 53, 70, 0.2);
  padding: 0.875rem 2rem;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  transition: background 0.2s;
}

.section-hero-soft-pastel-x2 .hero-visual {
  position: relative;
}

.section-hero-soft-pastel-x2 .glass-card {
  background: rgba(255, 255, 255, 0.6);
  backdrop-filter: blur(12px);
  border: 1px solid rgba(255, 255, 255, 0.8);
  padding: 1rem;
  border-radius: 24px;
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.1);
  transform: rotate(-2deg);
  transition: transform 0.3s ease;
}

.section-hero-soft-pastel-x2 .glass-card:hover {
  transform: rotate(0deg);
}

.section-hero-soft-pastel-x2 .hero-img {
  width: 100%;
  height: auto;
  border-radius: 16px;
  display: block;
}

.section-hero-soft-pastel-x2 .floating-review {
  position: absolute;
  bottom: -20px;
  right: -20px;
  background: var(--primary-dark);
  color: #fff;
  padding: 1.25rem;
  border-radius: 16px;
  box-shadow: 0 10px 20px rgba(0,0,0,0.15);
  max-width: 220px;
}

.section-hero-soft-pastel-x2 .floating-review .stars {
  color: #fbbf24;
  font-size: 0.875rem;
  margin-bottom: 0.5rem;
}

.section-hero-soft-pastel-x2 .floating-review p {
  font-size: 0.9rem;
  margin: 0 0 0.5rem 0;
  font-style: italic;
}

.section-hero-soft-pastel-x2 .floating-review small {
  font-size: 0.75rem;
  opacity: 0.7;
}

js:
(() => {
  const section = document.querySelector('.section-hero-soft-pastel-x2');
  if (!section) return;
  const images = section.querySelectorAll('img');
  images.forEach(img => {
    if (img.src.startsWith('https://pixabay.com/api/')) {
      fetch(img.src).then(r => r.json()).then(d => {
        if(d.hits && d.hits.length) img.src = d.hits[0].webformatURL;
      });
    }
  });
})();
