css:
.cover-section-food {
  font-family: 'Quicksand', sans-serif;
  background-color: #fcfaf7;
  padding: 60px 20px;
}
.cover-section-food .container {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 40px;
}
.cover-section-food .content {
  flex: 1;
  text-align: center;
  max-width: 500px;
}
.cover-section-food .title {
  font-size: 46px;
  font-weight: 700;
  color: #4d633f;
  margin-bottom: 15px;
  line-height: 1.3;
}
.cover-section-food .subtitle {
  font-size: 19px;
  color: #7a8c6b;
  margin-bottom: 30px;
  line-height: 1.7;
}
.cover-section-food .actions {
  display: flex;
  justify-content: center;
  gap: 15px;
  flex-wrap: wrap;
}
.cover-section-food .btn-primary {
  background-color: #81c14b;
  color: #ffffff;
  padding: 13px 28px;
  border: none;
  border-radius: 6px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s ease;
}
.cover-section-food .btn-primary:hover {
  background-color: #6aa83e;
}
.cover-section-food .btn-secondary {
  background-color: transparent;
  color: #81c14b;
  padding: 13px 28px;
  border: 1px solid #81c14b;
  border-radius: 6px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cover-section-food .btn-secondary:hover {
  background-color: #81c14b;
  color: #ffffff;
}
.cover-section-food .image-container {
  flex: 1;
  width: 100%;
  max-width: 550px;
}
.cover-section-food .image-container img {
  width: 100%;
  height: auto;
  display: block;
  border-radius: 10px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

@media (min-width: 768px) {
  .cover-section-food .container {
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
  }
  .cover-section-food .content {
    text-align: left;
    flex: 0 0 50%;
    max-width: 480px;
  }
  .cover-section-food .image-container {
    flex: 0 0 45%;
    max-width: 520px;
  }
  .cover-section-food .title {
    font-size: 50px;
  }
  .cover-section-food .subtitle {
    font-size: 20px;
  }
}

@media (min-width: 992px) {
  .cover-section-food .title {
    font-size: 56px;
  }
  .cover-section-food .subtitle {
    font-size: 21px;
  }
}

