.cover-section-wellness {
  font-family: 'Merriweather', serif;
  background-color: #f8f5f2;
  padding: 70px 20px;
}
.cover-section-wellness .container {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 40px;
}
.cover-section-wellness .content {
  flex: 1;
  text-align: left;
  max-width: 500px;
}
.cover-section-wellness .title {
  font-size: 48px;
  font-weight: 700;
  color: #3a4d39;
  margin-bottom: 18px;
  line-height: 1.25;
}
.cover-section-wellness .subtitle {
  font-size: 19px;
  color: #738c7a;
  margin-bottom: 30px;
  line-height: 1.7;
}
.cover-section-wellness .features {
  list-style: none;
  padding: 0;
  margin-bottom: 30px;
}
.cover-section-wellness .features li {
  display: flex;
  align-items: center;
  margin-bottom: 12px;
  font-size: 16px;
  color: #4a5568;
}
.cover-section-wellness .features .icon {
  color: #8fbc8f;
  margin-right: 10px;
  font-weight: bold;
}
.cover-section-wellness .actions {
  display: flex;
  gap: 15px;
  flex-wrap: wrap;
}
.cover-section-wellness .btn-primary {
  background-color: #5a8f7b;
  color: #ffffff;
  padding: 13px 28px;
  border: none;
  border-radius: 5px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s ease;
}
.cover-section-wellness .btn-primary:hover {
  background-color: #4a7a69;
}
.cover-section-wellness .btn-secondary {
  background-color: transparent;
  color: #5a8f7b;
  padding: 13px 28px;
  border: 1px solid #5a8f7b;
  border-radius: 5px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cover-section-wellness .btn-secondary:hover {
  background-color: #5a8f7b;
  color: #ffffff;
}
.cover-section-wellness .image-container {
  flex: 1;
  max-width: 600px;
}
.cover-section-wellness .image-container img {
  width: 100%;
  height: auto;
  display: block;
  border-radius: 8px;
  box-shadow: 0 5px 16px rgba(0, 0, 0, 0.1);
}

@media (min-width: 768px) {
  .cover-section-wellness .container {
    flex-direction: row;
    justify-content: space-between;
  }
  .cover-section-wellness .content {
    text-align: left;
    flex: 0 0 50%;
    max-width: 500px;
  }
  .cover-section-wellness .image-container {
    flex: 0 0 45%;
    max-width: 550px;
  }
  .cover-section-wellness .title {
    font-size: 52px;
  }
  .cover-section-wellness .subtitle {
    font-size: 20px;
  }
}

@media (min-width: 992px) {
  .cover-section-wellness .title {
    font-size: 60px;
  }
  .cover-section-wellness .subtitle {
    font-size: 21px;
  }
}

