css:
.split-hero-container-1 {
  display: flex;
  flex-direction: column;
  background-color: #F8F9FA;
  font-family: Arial, sans-serif;
  overflow: hidden;
  min-height: 80vh;
}
.split-hero-content-1 {
  padding: 40px 20px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  text-align: center;
}
.split-hero-heading-1 {
  font-size: 2.5rem;
  color: #212529;
  margin-bottom: 16px;
  font-weight: 700;
}
.split-hero-subheading-1 {
  font-size: 1.1rem;
  color: #6C757D;
  line-height: 1.6;
  margin-bottom: 32px;
  max-width: 500px;
  margin-left: auto;
  margin-right: auto;
}
.split-hero-cta-1 {
  background-color: #007BFF;
  color: #FFFFFF;
  padding: 14px 28px;
  border-radius: 5px;
  text-decoration: none;
  font-weight: bold;
  transition: background-color 0.3s ease, transform 0.3s ease;
  display: inline-block;
  align-self: center;
}
.split-hero-cta-1:hover {
  background-color: #0056b3;
  transform: translateY(-2px);
}
.split-hero-image-wrapper-1 {
  width: 100%;
  height: 300px;
}
.split-hero-image-1 {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

@media (min-width: 768px) {
  .split-hero-container-1 {
    flex-direction: row;
  }
  .split-hero-content-1 {
    width: 50%;
    padding: 60px;
    text-align: left;
    align-items: flex-start;
  }
  .split-hero-subheading-1 {
    margin-left: 0;
    margin-right: 0;
  }
  .split-hero-cta-1 {
    align-self: flex-start;
  }
  .split-hero-image-wrapper-1 {
    width: 50%;
    height: auto;
  }
}

@media (min-width: 1024px) {
  .split-hero-heading-1 {
    font-size: 3.5rem;
  }
  .split-hero-subheading-1 {
    font-size: 1.25rem;
  }
}
