css:
.art-nouveau-cta-emerald-gold-floral-border {
  background-color: #fff;
  color: #1e3a30; /* Deep Emerald */
  padding: 50px 25px;
  text-align: center;
}
.art-nouveau-cta-emerald-gold-floral-border .container {
  max-width: 1100px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
}
.art-nouveau-cta-emerald-gold-floral-border .content {
  margin-bottom: 30px;
}
.art-nouveau-cta-emerald-gold-floral-border h2 {
  font-family: 'Libre Baskerville', serif;
  font-size: 2.6em;
  margin-bottom: 15px;
  color: #b8860b; /* DarkGoldenrod */
}
.art-nouveau-cta-emerald-gold-floral-border p {
  font-family: 'Libre Baskerville', serif;
  font-size: 1.15em;
  line-height: 1.6;
  margin-bottom: 30px;
}
.art-nouveau-cta-emerald-gold-floral-border .cta-button {
  display: inline-block;
  padding: 16px 32px;
  background-color: #b8860b; /* DarkGoldenrod */
  color: #fff;
  text-decoration: none;
  font-family: 'Libre Baskerville', serif;
  font-weight: bold;
  border-radius: 5px;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.art-nouveau-cta-emerald-gold-floral-border .cta-button:hover {
  background-color: #1e3a30; /* Deep Emerald */
  color: #fff;
}
.art-nouveau-cta-emerald-gold-floral-border .image-container {
  width: 100%;
  max-width: 450px;
  margin-top: 20px;
  border: 5px solid #b8860b;
  border-radius: 8px;
  overflow: hidden;
}
.art-nouveau-cta-emerald-gold-floral-border .image-container img {
  display: block;
  width: 100%;
  height: auto;
}

@media (min-width: 768px) {
  .art-nouveau-cta-emerald-gold-floral-border .container {
    flex-direction: row;
    justify-content: space-between;
    align-items: flex-start;
  }
  .art-nouveau-cta-emerald-gold-floral-border .content {
    flex: 1;
    padding-right: 40px;
    text-align: left;
  }
  .art-nouveau-cta-emerald-gold-floral-border .image-container {
    flex-basis: 50%;
    margin-top: 0;
  }
  .art-nouveau-cta-emerald-gold-floral-border h2 {
    font-size: 3em;
  }
  .art-nouveau-cta-emerald-gold-floral-border p {
    font-size: 1.2em;
  }
}
