css:
.art-nouveau-cta-emerald-gold .container { display: flex; flex-direction: column; align-items: center; padding: 40px 20px; background-color: #1e3a30; /* Deep Emerald */ color: #f5f5dc; /* Cream */ }
.art-nouveau-cta-emerald-gold .content { text-align: center; margin-bottom: 30px; }
.art-nouveau-cta-emerald-gold h2 { font-family: 'Playfair Display', serif; font-size: 2.5em; margin-bottom: 15px; color: #d4af37; /* Muted Gold */ }
.art-nouveau-cta-emerald-gold p { font-family: 'Lato', sans-serif; font-size: 1.1em; line-height: 1.6; margin-bottom: 30px; }
.art-nouveau-cta-emerald-gold .cta-button { display: inline-block; padding: 15px 30px; background-color: #d4af37; /* Muted Gold */ color: #1e3a30; /* Deep Emerald */ text-decoration: none; font-family: 'Lato', sans-serif; font-weight: bold; border-radius: 5px; transition: background-color 0.3s ease, color 0.3s ease; }
.art-nouveau-cta-emerald-gold .cta-button:hover { background-color: #f5f5dc; /* Cream */ color: #1e3a30; /* Deep Emerald */ }
.art-nouveau-cta-emerald-gold .image-container { width: 100%; max-width: 400px; }
.art-nouveau-cta-emerald-gold .image-container img { display: block; width: 100%; height: auto; border-radius: 8px; box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3); }

@media (min-width: 768px) {
  .art-nouveau-cta-emerald-gold .container { flex-direction: row; text-align: left; padding: 60px 40px; }
  .art-nouveau-cta-emerald-gold .content { flex: 1; margin-right: 40px; margin-bottom: 0; }
  .art-nouveau-cta-emerald-gold .image-container { flex: 1; }
  .art-nouveau-cta-emerald-gold h2 { font-size: 3em; }
  .art-nouveau-cta-emerald-gold p { font-size: 1.2em; }
}
