css:
.art-nouveau-cta-emerald-motif-cream {
  background-color: #f5f5dc; /* Cream */
  color: #006400; /* Dark Green */
  padding: 50px 25px;
  text-align: center;
}
.art-nouveau-cta-emerald-motif-cream .container {
  max-width: 1100px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
}
.art-nouveau-cta-emerald-motif-cream .image-container {
  width: 100%;
  max-width: 400px;
  margin-bottom: 30px;
  border: 4px solid #228b22; /* Forest Green */
  border-radius: 5px;
  padding: 5px;
  box-shadow: 0 4px 10px rgba(0,0,0,0.2);
}
.art-nouveau-cta-emerald-motif-cream .image-container img {
  display: block;
  width: 100%;
  height: auto;
  border-radius: 5px;
}
.art-nouveau-cta-emerald-motif-cream .content {
  color: #2f4f4f; /* Dark Slate Gray */
}
.art-nouveau-cta-emerald-motif-cream h2 {
  font-family: 'Times New Roman', serif;
  font-size: 2.6em;
  margin-bottom: 15px;
  color: #006400; /* Dark Green */
}
.art-nouveau-cta-emerald-motif-cream p {
  font-family: 'Times New Roman', serif;
  font-size: 1.15em;
  line-height: 1.6;
  margin-bottom: 30px;
}
.art-nouveau-cta-emerald-motif-cream .cta-button {
  display: inline-block;
  padding: 15px 30px;
  background-color: #006400; /* Dark Green */
  color: #f5f5dc; /* Cream */
  text-decoration: none;
  font-family: 'Times New Roman', serif;
  font-weight: bold;
  border-radius: 5px;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.art-nouveau-cta-emerald-motif-cream .cta-button:hover {
  background-color: #f5f5dc; /* Cream */
  color: #006400; /* Dark Green */
}

@media (min-width: 768px) {
  .art-nouveau-cta-emerald-motif-cream .container {
    flex-direction: row;
    justify-content: space-between;
    align-items: flex-start;
  }
  .art-nouveau-cta-emerald-motif-cream .image-container {
    flex-basis: 45%;
    margin-bottom: 0;
  }
  .art-nouveau-cta-emerald-motif-cream .content {
    flex: 1;
    padding-left: 40px;
    text-align: left;
  }
  .art-nouveau-cta-emerald-motif-cream h2 {
    font-size: 3em;
  }
  .art-nouveau-cta-emerald-motif-cream p {
    font-size: 1.2em;
  }
}
