css:
.art-nouveau-cta-intricate-design-muted-gold {
  background-color: #5d4037; /* Earthy Brown */
  color: #e0cfae; /* Muted Cream/Beige */
  padding: 50px 25px;
  text-align: center;
}
.art-nouveau-cta-intricate-design-muted-gold .container {
  max-width: 1100px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
}
.art-nouveau-cta-intricate-design-muted-gold .image-container {
  width: 100%;
  max-width: 450px;
  margin-bottom: 30px;
}
.art-nouveau-cta-intricate-design-muted-gold .image-container img {
  display: block;
  width: 100%;
  height: auto;
  border-radius: 8px;
  border: 3px solid #b8860b; /* DarkGoldenrod */
}
.art-nouveau-cta-intricate-design-muted-gold .content {
  color: #fff;
}
.art-nouveau-cta-intricate-design-muted-gold h2 {
  font-family: 'Playfair Display', serif;
  font-size: 2.5em;
  margin-bottom: 15px;
  color: #b8860b; /* DarkGoldenrod */
}
.art-nouveau-cta-intricate-design-muted-gold p {
  font-family: 'Lato', sans-serif;
  font-size: 1.1em;
  line-height: 1.6;
  margin-bottom: 30px;
}
.art-nouveau-cta-intricate-design-muted-gold .cta-button {
  display: inline-block;
  padding: 15px 30px;
  background-color: #b8860b; /* DarkGoldenrod */
  color: #5d4037; /* Earthy Brown */
  text-decoration: none;
  font-family: 'Lato', sans-serif;
  font-weight: bold;
  border-radius: 5px;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.art-nouveau-cta-intricate-design-muted-gold .cta-button:hover {
  background-color: #e0cfae; /* Muted Cream/Beige */
  color: #5d4037; /* Earthy Brown */
}

@media (min-width: 768px) {
  .art-nouveau-cta-intricate-design-muted-gold .container {
    flex-direction: row;
    justify-content: space-between;
    text-align: left;
  }
  .art-nouveau-cta-intricate-design-muted-gold .image-container {
    flex-basis: 45%;
    margin-bottom: 0;
  }
  .art-nouveau-cta-intricate-design-muted-gold .content {
    flex: 1;
    padding-left: 40px;
  }
  .art-nouveau-cta-intricate-design-muted-gold h2 {
    font-size: 2.8em;
  }
  .art-nouveau-cta-intricate-design-muted-gold p {
    font-size: 1.15em;
  }
}
