css:
.art-nouveau-cta-jewel-tones-decorative {
  background-color: #8b008b; /* Dark Magenta */
  color: #fff;
  padding: 50px 25px;
  text-align: center;
}
.art-nouveau-cta-jewel-tones-decorative .container {
  max-width: 1100px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
}
.art-nouveau-cta-jewel-tones-decorative .content {
  margin-bottom: 30px;
}
.art-nouveau-cta-jewel-tones-decorative h2 {
  font-family: 'Uncial Antiqua', cursive;
  font-size: 2.5em;
  margin-bottom: 15px;
  color: #ffd700; /* Gold */
  letter-spacing: 0.5px;
}
.art-nouveau-cta-jewel-tones-decorative p {
  font-family: 'Uncial Antiqua', cursive;
  font-size: 1.15em;
  line-height: 1.6;
  margin-bottom: 30px;
}
.art-nouveau-cta-jewel-tones-decorative .cta-button {
  display: inline-block;
  padding: 16px 32px;
  background-color: #ffd700; /* Gold */
  color: #8b008b; /* Dark Magenta */
  text-decoration: none;
  font-family: 'Uncial Antiqua', cursive;
  font-weight: bold;
  border-radius: 5px;
  transition: background-color 0.3s ease, color 0.3s ease;
  border: 2px solid #ffd700;
}
.art-nouveau-cta-jewel-tones-decorative .cta-button:hover {
  background-color: transparent;
  color: #ffd700; /* Gold */
}
.art-nouveau-cta-jewel-tones-decorative .ornament-container {
  width: 100%;
  max-width: 300px;
  margin-top: 20px;
}
.art-nouveau-cta-jewel-tones-decorative .ornament-container img {
  display: block;
  width: 100%;
  height: auto;
  border-radius: 8px;
  opacity: 0.75;
}

@media (min-width: 768px) {
  .art-nouveau-cta-jewel-tones-decorative .container {
    flex-direction: row;
    justify-content: space-between;
  }
  .art-nouveau-cta-jewel-tones-decorative .content {
    flex: 1;
    text-align: left;
    padding-right: 40px;
    margin-bottom: 0;
  }
  .art-nouveau-cta-jewel-tones-decorative .ornament-container {
    flex-basis: 35%;
    max-width: none;
    margin-top: 0;
  }
  .art-nouveau-cta-jewel-tones-decorative h2 {
    font-size: 3em;
  }
  .art-nouveau-cta-jewel-tones-decorative p {
    font-size: 1.2em;
  }
}
