css:
.art-nouveau-cta-jewel-tones-split .container {
  display: flex;
  flex-direction: column;
  background-color: #8a2be2; /* Blue Violet (jewel tone) */
  color: #fff;
}
.art-nouveau-cta-jewel-tones-split .text-content {
  padding: 40px 20px;
  background-color: #8a2be2; /* Blue Violet */
  color: #fff0f5; /* Lavender Blush (soft cream) */
  text-align: center;
}
.art-nouveau-cta-jewel-tones-split h2 {
  font-family: 'Cinzel', serif;
  font-size: 2.2em;
  margin-bottom: 15px;
  color: #ffd700; /* Gold */
}
.art-nouveau-cta-jewel-tones-split p {
  font-family: 'Cinzel', serif;
  font-size: 1.1em;
  line-height: 1.6;
  margin-bottom: 30px;
}
.art-nouveau-cta-jewel-tones-split .cta-button {
  display: inline-block;
  padding: 14px 28px;
  background-color: #ffd700; /* Gold */
  color: #8a2be2; /* Blue Violet */
  text-decoration: none;
  font-family: 'Cinzel', serif;
  font-weight: bold;
  border-radius: 5px;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.art-nouveau-cta-jewel-tones-split .cta-button:hover {
  background-color: #fff0f5; /* Lavender Blush */
  color: #8a2be2; /* Blue Violet */
}
.art-nouveau-cta-jewel-tones-split .image-content {
  width: 100%;
}
.art-nouveau-cta-jewel-tones-split .image-content img {
  display: block;
  width: 100%;
  height: auto;
  object-fit: cover;
}

@media (min-width: 768px) {
  .art-nouveau-cta-jewel-tones-split .container {
    flex-direction: row;
  }
  .art-nouveau-cta-jewel-tones-split .text-content {
    flex: 1;
    padding: 60px 40px;
    text-align: left;
  }
  .art-nouveau-cta-jewel-tones-split .image-content {
    flex: 1;
  }
  .art-nouveau-cta-jewel-tones-split h2 {
    font-size: 2.6em;
  }
  .art-nouveau-cta-jewel-tones-split p {
    font-size: 1.15em;
  }
}
